// SDF main function
// calculates distance from items in the scene
float SDF();

vec2 mouseScale;
vec2 uv;

// sined distance function of a paraboloid at Z
// https://en.wikipedia.org/wiki/Signed_distance_function
float paraboloidZSDF(vec2 o, vec3 p, float a, float b) {
    float z = (pow(p.x - o.x, 2.)/pow(a, 2.)) + (pow(p.y - o.y, 2.)/pow(b, 2.));

    return z - p.z;
}

float planeSDF(float y, vec3 p) {
    return p.y - y;
}

float SDF(vec3 p) {
    return min(
            paraboloidZSDF(vec2(0., 0.), p, 1., 1.),
            planeSDF(-5., p)
            );
}

vec3 getNormal(vec3 p) {
    float d = SDF(p);
    vec2 e = vec2(.01, 0);

    vec3 n = d - vec3(
        SDF(p - e.xyy),
        SDF(p - e.yxy),
        SDF(p - e.yyx));

    return normalize(n);
}

float light(vec3 o, vec3 view) {
    vec3 l = normalize(o - view);
    vec3 n = getNormal(view);
    float distView = length(o - view);

    float dif = clamp(dot(n, l), 0., 1.);

    float d = length(rayMarch(view + n * 0.02, l));
    if (d < distView) dif *= .5;

    return dif;
}

vec3 rayMarch(vec3 o, vec3 d) {
    vec3 ray;
    vec3 mouseDir = d;
    float t = 0.;

    for (int i = 0; i < 128; i++) {
        ray = o + mouseDir * t;

        float dist = SDF(ray);

    	if(dist < 0.001) break;

		t += dist;
    }

    return ray;
}

