// By Lucas
// Simple Maze GoL (B3/S12345)

#define START_SIZE 4.0
#define START_FRAME 64
#define SEED 91.34583

float rand(float x) {
    return fract(sin(x * SEED) * 47453.5453);
}

vec3 rand3(float x) {
    return vec3(rand(x) > 0.5 ? 1.0 : 0.0);
}

bool alive(ivec2 pos) {
    vec3 tex = texelFetch(iChannel0, pos, 0).rgb;
    return tex.r > 0.0 ||
           tex.g > 0.0 ||
           tex.b > 0.0;
}

vec3 colorize(vec3 col, vec2 uv) {
    return col + 0.5 + 0.5 * cos(iTime * 0.1 + uv.xyx + vec3(0, 2, 4));
}

void mainImage(out vec4 C, in vec2 P)
{    
    // Get neighbors in 3x3 square around pixel (Moore neighborhood)
    int count = 0;
    for (int y = -1; y < 2; ++y) {
        for (int x = -1; x < 2; ++x) {
            if (alive(ivec2(P.x + float(x), P.y + float(y)))) {
                count += 1;
                if (x == 0 && y == 0) {
                    count -= 1;
                }
            }
        }
    }

    bool state = alive(ivec2(P.xy));
    bool alive = false;
    // If alive, check whether pixel has enough neighbors (this is S)
    if (state && (count > 0 && count < 6)) { // S12345
        alive = true;
    }

    // If dead, check whether pixel is allowed to live (this is B)
    else if (count == 3) { // B3
        alive = true;
    }

    vec2 uv = P / iResolution.xy;
    vec3 col = texelFetch(iChannel0, ivec2(P.xy), 0).rgb;
    col = (alive == state) ? col :
          ((alive) ? colorize(col, uv) :
                     vec3(0.0));

    C = vec4(col, 1.0);

    // Pseudo random center starting blob (allows for going fullscreen before start)
    if (iFrame == START_FRAME) {
        if (P.x > iResolution.x / 2.0 - START_SIZE && P.x < iResolution.x / 2.0 + START_SIZE) {
            if (P.y > iResolution.y / 2.0 - START_SIZE && P.y < iResolution.y / 2.0 + START_SIZE) {
                C = vec4(colorize(rand3(P.x * P.y * (iTime + 1.0))*-1.0, uv), 1.0);            
            }
        }
    }
}

