            + .1 * hh(4. * t, 4. * t) 
            + .1 * fx(tfx + mod(1.001 * t, 8.), t) 
            + .2 * arp(t, t) 
            + 1. / float(16u) * mix(r.xy, r.wz, sin(t) * .5 + .5));
}

