    p.y=abs(p.y)-5.0;
    float dLinea1= sdBox(p-vec3(0.0,0.0,-8.0), vec3(22.0,0.15,0.5) );
    float dLinea2= sdBox(p-vec3(0.0,0.5,-8.0), vec3(22.0,0.15,0.5) );

    float dfin= Difference(dcuartoGr, dcuartoCh);
    dfin= Union(dfin, dLinea1);
    dfin= Union(dfin, dLinea2);

    float dcuartofin=dfin;

    p=p0;
    p.x= opRep1D(p.x, 8.0 );
    float dventanaA= sdBox(p, vec3(3.5,3.0,8.5) );

    p.z=abs(p.z)-7.0;
    float dMarcoA= sdBox(p, vec3(3.6,3.0,0.7) );
    float dMarcoB= sdBox(p, vec3(3.6,3.0,0.5) );

    p=p0;
    p.x= opRep1D(p.x, 8.0 );
    p.y= opRep1D(p.y, 2.0 );
    p.z=abs(p.z)-7.0;
    float dBarroteCH= sdBox(p, vec3(3.0,0.25,1.0) );
    p=p0;
    p.z=abs(p.z)-7.0;
    p.x=opRep1D(p.x, 2.0 );
    float dBarroteCV= sdBox(p, vec3(0.25,3.5,1.0) );

    dfin= Difference(dfin, dventanaA);

    dMarcoA=Difference(dMarcoA, dBarroteCH);
    dMarcoA=Difference(dMarcoA, dBarroteCV);
    dMarcoB=Difference(dMarcoB,dMarcoA);
    dMarcoB=Intersect(dcuartofin,dMarcoB);
    res =opU2(res, vec2(dfin,16.0));
    res =opU2(res, vec2(dMarcoB,2.0));
    return res;
}

