float offset[3] = float[](.05,.75,2.25);
float weight[3] = float[](.2,.25,.075);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  
	vec2 uv = fragCoord.xy/iResolution.xy;

    vec3 C = texture(iChannel0, uv).rgb;
    vec3 R = texture(iChannel0, uv).rgb * weight[0];
    for (int i=1; i<3; i++) {
      R += texture(iChannel0, uv + vec2(offset[i])/iResolution.xy, .0).rgb * weight[i];
      R += texture(iChannel0, uv - vec2(offset[i])/iResolution.xy, .0).rgb * weight[i];
    }

    // mask for effect and mixdown 
    float mask = distance(uv.xy,vec2(.5));
    mask = smoothstep(.52,.965,1.-mask);
    vec3 CR = mix(R,C,mask);

    // output
    CR=pow(CR, vec3(.4545));
    fragColor = vec4(CR,1.);
}

