const float scale = 4.;
const float quad = scale/2.;
const float cell = quad/2.;
const float spce = cell/2.;
const vec2 l = vec2(scale,scale);
const vec2 s = l*2.;

const float density = 12.;
const float dshalf = density/2.;
float shorten = 1.5, lpscale=0., movement=0., time=0.;

vec2 map(vec3 q3) {
    vec2 res = vec2(1e5,0.);

    q3=-q3;
    q3.y-=.5;
    q3.xz*=rot(T*.152);

    // log-spherical map
    float r = length(q3);
    float mul = r/lpscale;
    q3 = vec3(log(r), acos(q3.y / r ), atan(q3.z, q3.x));
    q3 *= lpscale;
    q3 -= vec3(movement,dshalf,2.);

    vec2 p,
         ip,
         id = vec2(0),
         ct = vec2(0);

    const vec2[4] ps4 = vec2[4](vec2(-.5, .5), vec2(.5),   vec2(.5, -.5), vec2(-.5));

    float ld=1e5,lf=1e5,lb=1e5,bx=1e5;

    // Multi Tap distance map picked up from @Shane
    // https://www.shadertoy.com/view/WtffDS
    for(int i = 0; i<4; i++) {

        ct = ps4[i]/2. -  ps4[0]/2.;	// Block center.
        p = q3.xz - ct*s;				// Local coordinates. 
        ip = floor(p/s) + .5;			// Local tile ID. 
        p -= (ip)*s; 					// New local position.		   
        vec2 idi = (ip + ct)*s;			// Correct position & tile ID.

        vec3 q = vec3(p.x,q3.y,p.y);

        vec3 q1 = vec3(p.x+cell,q3.y,p.y+cell);

        q1.xz=abs(abs(q1.xz)-scale)-quad;

        if(tmod<1.){
            q1.z+=ga1*quad;
            q1.yz*=rg2;
        }else if(tmod<2.){
            q1.z+=quad;
            q1.x+=ga2*quad;
            q1.yz*=rnp;
            q1.yx*=rg3;

        }else if(tmod<3.){
            q1.z-=quad;
            q1.x-=quad;
            q1.z+=ga3*quad;
            q1.yz*=rg2a;
        } else {
            q1.x+=quad;
            q1.x-=ga4*quad;
            q1.yx*=rpi;
            q1.yx*=rg3a;
        }

        float s1 = length(q1)-.65;

        s1=max(box(q1+vec3(spce,0,spce),vec3(.5,1,.5)),s1);
        ld = min(ld,s1);

        if(ld<res.x) {
            res = vec2(ld,1.);
            hp=q3-cell;
        }
        q.xz=abs(abs(q.xz)-quad)-cell;
        lb = min( capp(q,.05,.75), bx); 
        lb = min( capp(q,.05,.75), lb);
    }

    if(lb<res.x) {
        res = vec2(lb,5.);
        hp=q3;
    }

    float fx = q3.y+.625;
    if(fx<res.x) {
        res = vec2(fx,6.);
        hp=q3;
    }

    res.x *= mul/shorten;
    return res;
}

// Tetrahedron technique @iq
// https://iquilezles.org/articles/normalsSDF
vec3 normal(vec3 p, float t) {
    float e = MIN_DIST*t;
    vec2 h =vec2(1,-1)*.5773;
    vec3 n = h.xyy * map(p+h.xyy*e).x+
             h.yyx * map(p+h.yyx*e).x+
             h.yxy * map(p+h.yxy*e).x+
             h.xxx * map(p+h.xxx*e).x;
    return normalize(n);
}

vec2 marcher(vec3 ro, vec3 rd, inout vec3 p, inout bool hit, int steps) {
    hit = false; float d=0., m = 0.;
    for(int i=0;i<steps;i++)
    {
        vec2 t = map(p);
        if(t.x<1e-8) hit = true;
        d += i<32? t.x*.5:t.x;
        m  = t.y;
        p = ro + rd * d;
        if(d>175.) break;
    } 
    return vec2(d,m);
}

//@iq of hsv2rgb
vec3 hsv2rgb( in vec3 c ) {
    vec3 rgb = clamp( abs(mod(c.x*4.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
	return c.z * mix( vec3(1.0), rgb, c.y);
}
vec3 render(inout vec3 ro, inout vec3 rd, inout vec3 ref, int bnc, inout float d) {

