#define PIXEL 1./iResolution.y   // Size of a pixel in UV coordinates
#define TIME (iTime*SPEED)

// GLOBAL PARAMETERS
#define BALL 0.007     // Radius of the player in UV coordinates
#define WALL 0.002     // Wall thickness in UV coordinates
#define SPEED 1.       // Game speed
#define DANGER 1.      // Time between danger appearance and wall appearance
#define VY 0.          // Vertical level translation speed
#define VX 0.05        // Horizontal level translation speed

// COLORS
#define backCOLOR   vec3(0.1, 0.2, 0.1)    // Background Color
#define dangerCOLOR vec3(1.0, 0.5, 0.0)    // Danger zone Color
#define loseCOLOR   vec3(0.9, 0.1, 0.1)    // Lose screen Color
#define wallCOLOR   vec3(0.7, 0.7, 0.7)    // Wall Color
#define ballCOLOR   vec3(1.0, 0.6, 0.6)    // Player Color


////////////////////////////////////////////////////////////////////////////////////////////
// LEVEL
////////////////////////////////////////////////////////////////////////////////////////////
//   To create your own level, remove everything in this
// section and replace the WallSDF function by your own
// function. It has to be the distance function to the
// walls of your level, and it can depend on time.
////////////////////////////////////////////////////////////////////////////////////////////

// PARAMETERS SPECIFIC TO THIS LEVEL
#define GRID 0.1       // Size of a cell in in UV coordinates
#define RATE 1.        // Wall switchings speed Rate
#define SEED 125.      // Level seed

// Functions used for WallSDF 
float rand(vec2 p, float time){
    float r = fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453 + SEED + RATE*time/40.);
    if (r>0.5){ return 1.; }
    else      { return 0.; }
}
float diagSDF(float x, float y){
    return min(min(
                abs(x-y)/sqrt(2.),
                abs(x-y-1.)/sqrt(2.)),
                abs(x-y+1.)/sqrt(2.));
}

// The 2D SDF function of the walls at point p and time t.
float WallSDF(vec2 p, float t){
    // Before starting the maze the level
    if (p.x<3.*GRID){
        p.y = mod(p.y+GRID/2.,GRID)-GRID/2.;
        float L = 3.*GRID;
        if (p.x+p.y>L)
            {return (p.y-p.x+L)/sqrt(2.);}
        if (p.x-p.y>L)
            {return (-p.y-p.x+L)/sqrt(2.);}
        return length(p-vec2(L,0.));
    } 
    // Inside the maze
    float xx = floor(p.x/GRID);
    float yy = floor(p.y/GRID);
    float dx = mod(p.x,GRID)/GRID;
    float dy = mod(p.y,GRID)/GRID;
    float orientation = rand(vec2(xx,yy), t);
    if (orientation==1.){ return GRID*diagSDF(   dx,dy); }
    else                { return GRID*diagSDF(1.-dy,dx); }
}


////////////////////////////////////////////////////////////////////////////////////////////
// GAME ENGINE
////////////////////////////////////////////////////////////////////////////////////////////

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // ENGINE
    // Compute uv coordinates
    vec2 uv = (fragCoord.xy-0.5*iResolution.xy) / iResolution.y;
    vec2 M = (iMouse.xy-0.5*iResolution.xy) / iResolution.y;
    // Level translation
    uv += vec2(VX,VY)*TIME;
    M += vec2(VX,VY)*TIME;
    // Compute the walls SDF at TIME+DANGER
    float danger = WallSDF(uv, TIME+DANGER);
    // Compute the walls SDF at TIME
    float wall = WallSDF(uv, TIME);

    // GRAPHICS
    // Print the background color
    vec3 color = backCOLOR;
    // Print a red screen if the player touches the walls
    if ( WallSDF(M, TIME) < WALL+BALL)
        { color = loseCOLOR; }
    // Print the danger zone color
    float dangerStep = smoothstep(PIXEL+WALL,WALL,danger);
    if (mod(fragCoord.x,2.)*mod(fragCoord.y,2.)<0.5) // Hatching effect 
        { color = mix(color, dangerCOLOR, dangerStep); }
    // Print the walls
    float wallStep = smoothstep(PIXEL+WALL,WALL,wall);
    color = mix(color, wallCOLOR, wallStep);
    // Print the ball
    float ball = sqrt((uv.x-M.x)*(uv.x-M.x)+(uv.y-M.y)*(uv.y-M.y));
    ball = smoothstep(BALL+PIXEL,BALL,ball);
    color = mix(color, ballCOLOR, ball);
    fragColor = vec4(color,1.0);
}

// Created by Walter Jansen (2022-09-04)
