//
// Editable variables at line 45 and in defines.

#define PI 3.1416
#define TAU (PI * 2.0)

#define SPOKES 9.0
#define MAX_VEL 1.0
#define DRAG_EFFECT 1.0
#define SWAY_FREQUENCY 1.0
#define SWAY_EFFECT 0.5
#define SWAY_MAG 0.2

float Sway(float dist, float radius, vec2 normal)
{
    vec2 velocity = (iMouse.xy / iResolution.xy - vec2(0.5,0.5)) * vec2(iResolution.x/iResolution.y * 2.0, 2.0);
    float speed;
    float offset;
    float sign;
    float mag;
    speed = min(MAX_VEL, length(velocity));
    velocity = normalize(velocity);
    offset = max(0.0, dist - radius);

    sign = smoothstep(-0.15, 0.15, dot(normal.yx * vec2(1,-1), velocity));
    sign = sign * 2.0 - 1.0;

    mag = (dot(normal, velocity) + 1.0) * DRAG_EFFECT;
    mag += sin(iTime * SWAY_FREQUENCY * TAU) * 0.2 * SWAY_EFFECT;
    mag *= speed * offset * sign;

    return (mag * SWAY_MAG);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;
    uv *= 2.;

    // EDITABLE Vars
    float radius = 0.42;
    float spokeThickness = 0.05;
    float spokeTransitionRatio = 1.1;
    float spokeTransitionLength = 0.11;
    float spokeRadius = 0.07;
    vec3 colorBG    = vec3(0.12, 0.15, 0.60);
    vec3 colorSpoke = vec3(1.00, 1.00, 1.00);
    vec3 colorBase  = vec3(0.40, 0.20, 0.20);
    vec3 colorEnds  = vec3(0.34, 0.34, 0.34);

    // Other
    float spokeDist = (1.0 - spokeRadius);

    // Polar coordinates
    float a;
    float d;
    a = atan(uv.y, uv.x) / (PI * 2.0) + 0.5; // Range 0.0 - 1.0
    d = length(uv);

    // Spoke Cell
    float c;
    c = floor(a * SPOKES); // Range 0 - (SPOKES - 1)

    // Spoke Normal
    float sway;
    vec2 normal;
    vec2 n;
    a = ((0.5 + c) / SPOKES) * TAU;
    normal = vec2(-cos(a), -sin(a));
    sway = Sway(d, radius, normal);
    n = vec2(-cos(a + sway), -sin(a + sway));

    vec2 uv2;
    uv2.x = dot(vec2(n.y, -n.x), uv);
    uv2.y = dot(n, uv);

    // Spoke Line
    float line;
    line = smoothstep(spokeThickness * 0.5 + 0.0025,spokeThickness * 0.5 - 0.0025, abs(uv2.x));
    line = line * smoothstep(spokeDist, spokeDist - 0.01, uv2.y);

    // Circle Shape
    float circle;
    float rAdd;  // Transition to spokes
    rAdd = (cos(min(max(0.0, abs(uv2.x)-spokeThickness*spokeTransitionRatio*0.5) / (spokeThickness * 1.9), 0.5) * TAU) + 1.0) * 0.5 * spokeTransitionLength;
    circle = smoothstep((radius + rAdd), (radius + rAdd)-0.008, d);

    // Spoke Circle
    float sCircle;
    vec2 p;
    sway = Sway(spokeDist, radius, normal);
    p = vec2(-cos(a + sway), -sin(a + sway)) * spokeDist;
    sCircle = smoothstep(spokeRadius, spokeRadius-0.008, length(uv-p));

    // Coloring
    vec3 color;
    color = mix(colorBG, colorSpoke, line);
    color = mix(color, colorBase, circle);
    color = mix(color, colorEnds, sCircle);

    fragColor = vec4(color,1.0);
}

#define SIZEX 300

vec2 getpos(vec2 uv)
{
    return textureLod(iChannel0,vec2((uv+0.5)/iResolution.xy),0.0).xy;
}

float sqr(float x)
{
    return x*x*x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;

    fragColor = vec4(0.,0.,0.,1.0);
    for (int i=0;i<SIZEX;i++) {
        float lr = length(getpos(vec2(i,0.))-uv);
        fragColor.xyz += vec3(1.,0.,0.)*clamp(1.-lr*100.,0.,1.);
        float lb = length(getpos(vec2(i,2.))-uv);
        fragColor.xyz += vec3(0.,0.,1.)*clamp(1.-lb*100.,0.,1.);
        float lg = length(getpos(vec2(i,4.))-uv);
        fragColor.xyz += vec3(0.,1.,0.)*clamp(1.-lg*100.,0.,1.);
        float lh = length(getpos(vec2(i,6.))-uv);
        fragColor.xyz += vec3(0.4,0.4,0.4)*clamp(1.-lh*100.,0.,1.);
    }

    fragColor.xyz = clamp(fragColor.xyz,vec3(0.),vec3(1.));
}

