#define RULE_R_R -0.1
#define RULE_R_B -0.05
#define RULE_R_G -0.01
#define RULE_R_H 0.01

#define RULE_B_R 0.1
#define RULE_B_B 0.05
#define RULE_B_G 0.
#define RULE_B_H 0.

#define RULE_G_R -0.01
#define RULE_G_B 0.
#define RULE_G_G 0.05
#define RULE_G_H 0.

#define RULE_H_R -0.09
#define RULE_H_B 0.
#define RULE_H_G 0.
#define RULE_H_H -0.01

#define SIZEX 300
//#define RANGE 0.9
#define RANGE 0.2

float hash1(float x){ return fract(fract(x*0.31830988618379067153776752674503)*fract(x*0.15915494309189533576888376337251)*265871.1723); }
//float hash1( float n ) { return fract(sin(n)*753.5453123); }
vec2 hash2( vec2 n ) { return fract(sin(n)*753.5453123); }
vec4 hash4( vec4 n ) { return fract(sin(n)*753.5453123); }

float sqr(float x)
{
    x=x*x;
    x=x*x;
    x=x*x;
    return x*x;
}

vec2 getpos(vec2 uv)
{
    return textureLod(iChannel0,vec2((uv+0.5)/iResolution.xy),0.0).xy;
}

vec2 rule(vec2 pos,float v,float g)
{
    vec2 f;
    for (int i=0;i<SIZEX;i++)
    {
        vec2 b = getpos(vec2(i,v));
//        float d = length(pos-b)/RANGE;
//        if (d>0. && d<1.) {
//            f += vec2(pos-b)*(g*0.1/(1./sqr(1.-d)-1.));
//        }
        float d = length(pos-b);
        if (d>0. && d<RANGE) {
            f += vec2(pos-b)*(g*0.01/d);
        }
    }
    return f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float scale = 1./iResolution.y;
    vec2 fc = fragCoord;
    fc -= fract(fc);
    vec2 pos = vec2(0.);
    if (iFrame==0)
    {
        if (fract(fc.y*0.5)<0.1)
            pos = vec2(hash1(dot(fc,vec2(1.,0.139755)))*iResolution.x*scale,hash1(dot(fc,vec2(0.54,1.2))+0.5));
    } else {
        pos = getpos(fc);
        if (fract(fc.y*0.5)<0.1) {
            vec2 vel = getpos(fc+vec2(0.,1.));
            pos += vel;
        } else {
            vec2 f=vec2(0.);
            vec2 vel = getpos(fc-vec2(0.,1.0));
            if (fc.y==1.) {
                f = rule(vel,0.,RULE_R_R);
                f += rule(vel,2.,RULE_R_B);
                f += rule(vel,4.,RULE_R_G);
                f += rule(vel,6.,RULE_R_H);
            }
            if (fc.y==3.) {
                f = rule(vel,0.,RULE_B_R);
                f += rule(vel,2.,RULE_B_B);
                f += rule(vel,4.,RULE_B_G);
                f += rule(vel,6.,RULE_B_H);
            }
            if (fc.y==5.) {
                f = rule(vel,0.,RULE_G_R);
                f += rule(vel,2.,RULE_G_B);
                f += rule(vel,4.,RULE_G_G);
                f += rule(vel,6.,RULE_G_H);
            }
            if (fc.y==7.) {
                f = rule(vel,0.,RULE_H_R);
                f += rule(vel,2.,RULE_H_B);
                f += rule(vel,4.,RULE_H_G);
                f += rule(vel,6.,RULE_H_H);
            }
            pos = (pos+f)*0.5;
            vel += pos;
            if (vel.x<0. || vel.x>iResolution.x*scale) pos.x*=-1.;
            if (vel.y<0. || vel.y>1.) pos.y*=-1.;
        }
    }
    fragColor = vec4(pos,0.,0.);
}

