// CC0: Refraction "madness"
//  Expandning a bit further on the previous refraction shader experimented with
//  tweaking refraction.
#define PI              3.141592654
#define TAU             (2.0*PI)
#define PHI             (sqrt(5.0)*0.5 + 0.5)

#define TIME            iTime
#define RESOLUTION      iResolution

#define TOLERANCE       0.0001
#define MAX_RAY_LENGTH  20.0
#define MAX_RAY_MARCHES 60
#define NORM_OFF        0.001
#define MAX_BOUNCES     5

mat3 g_rot  = mat3(1.0); 
vec3 g_mat  = vec3(0.0);
vec3 g_beer = vec3(0.0);

// License: WTFPL, author: sam hocevar, found: https://stackoverflow.com/a/17897228/418488
const vec4 hsv2rgb_K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
vec3 hsv2rgb(vec3 c) {
  vec3 p = abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www);
  return c.z * mix(hsv2rgb_K.xxx, clamp(p - hsv2rgb_K.xxx, 0.0, 1.0), c.y);
}
// License: WTFPL, author: sam hocevar, found: https://stackoverflow.com/a/17897228/418488
//  Macro version of above to enable compile-time constants
#define HSV2RGB(c)  (c.z * mix(hsv2rgb_K.xxx, clamp(abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www) - hsv2rgb_K.xxx, 0.0, 1.0), c.y))

const float hoff = 0.0;

const vec3 skyCol     = HSV2RGB(vec3(hoff+0.6, 0.86, 1.0));
const vec3 glowCol    = HSV2RGB(vec3(hoff+0.065, 0.8, 6.0));
const vec3 diffuseCol = HSV2RGB(vec3(hoff+0.6, 0.85, 1.0));
const vec3 lightPos   = vec3(0.0, 10.0, 0.0);

const float initt       = 0.1; 

// License: Unknown, author: nmz (twitter: @stormoid), found: https://www.shadertoy.com/view/NdfyRM
vec3 sRGB(vec3 t) {
  return mix(1.055*pow(t, vec3(1./2.4)) - 0.055, 12.92*t, step(t, vec3(0.0031308)));
}

// License: Unknown, author: Matt Taylor (https://github.com/64), found: https://64.github.io/tonemapping/
vec3 aces_approx(vec3 v) {
  v = max(v, 0.0);
  v *= 0.6f;
  float a = 2.51f;
  float b = 0.03f;
  float c = 2.43f;
  float d = 0.59f;
  float e = 0.14f;
  return clamp((v*(a*v+b))/(v*(c*v+d)+e), 0.0f, 1.0f);
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float box(vec2 p, vec2 b) {
  vec2 d = abs(p)-b;
  return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/www/articles/intersectors/intersectors.htm
float rayPlane(vec3 ro, vec3 rd, vec4 p) {
  return -(dot(ro,p.xyz)+p.w)/dot(rd,p.xyz);
}

mat3 rot_z(float a) {
  float c = cos(a);
  float s = sin(a);
  return mat3(
      c,s,0
    ,-s,c,0
    , 0,0,1
    );
}

mat3 rot_y(float a) {
  float c = cos(a);
  float s = sin(a);
  return mat3(
      c,0,s
    , 0,1,0
    ,-s,0,c
    );
}

mat3 rot_x(float a) {
  float c = cos(a);
  float s = sin(a);
  return mat3(
      1, 0,0
    , 0, c,s
    , 0,-s,c
    );
}

float sphere4(vec3 p, float r) {
  p *= p;
  return pow(dot(p, p), 0.25)-r;
}

float box(vec3 p, vec3 b) {
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

vec3 skyColor(vec3 ro, vec3 rd) {
  vec3 col = clamp(vec3(0.0025/abs(rd.y))*skyCol, 0.0, 1.0);

  float tp0  = rayPlane(ro, rd, vec4(vec3(0.0, 1.0, 0.0), 4.0));
  float tp1  = rayPlane(ro, rd, vec4(vec3(0.0, -1.0, 0.0), 6.0));
  float tp = tp1;
  tp = max(tp0,tp1);


  if (tp1 > 0.0) {
    vec3 pos  = ro + tp1*rd;
    vec2 pp = pos.xz;
    float db = box(pp, vec2(6.0, 9.0))-1.0;

    col += vec3(4.0)*skyCol*rd.y*rd.y*smoothstep(0.25, 0.0, db);
    col += vec3(0.8)*skyCol*exp(-0.5*max(db, 0.0));
  }

  if (tp0 > 0.0) {
    vec3 pos  = ro + tp0*rd;
    vec2 pp = pos.xz;
    float ds = length(pp) - 0.5;

    col += vec3(0.25)*skyCol*exp(-.5*max(ds, 0.0));
  }

  return clamp(col, 0.0, 10.0);
}

float df(vec3 p) {
  p *= g_rot;
  vec3 p0 = p;
  vec3 p1 = p;
  p1 *= g_rot;
  float d0 = box(p0, vec3(1.5, 1.5, 1.5))-0.025;
  float d1 = sphere4(p1, 1.0);
  float d2 = d1 - 0.5 + initt;
  float d3 = d1 + 0.3;

  const float refraction = 0.8;
  float d = d0;
  d = max(d, -d1);
  float dd = smoothstep(-0.5, 0.5, sin(-TIME+(20.0*(d2 - d0))));
  vec3 mat = vec3(0.8, 0.5, mix(0.8, -1.2, dd));
  vec3 beer = 1.0*vec3(0.1, 0.2, 0.05);

  if (d3 < d) {
    d = d3;
    mat = vec3(0.5, 0.5, 0.8);
    beer = -glowCol;
  }

  g_mat = mat;
  g_beer = beer;
  return d;
}

vec3 normal(vec3 pos) {
  vec2  eps = vec2(NORM_OFF,0.0);
  vec3 nor;
  nor.x = df(pos+eps.xyy) - df(pos-eps.xyy);
  nor.y = df(pos+eps.yxy) - df(pos-eps.yxy);
  nor.z = df(pos+eps.yyx) - df(pos-eps.yyx);
  return normalize(nor);
}

float rayMarch(vec3 ro, vec3 rd, float dfactor, out int ii) {
  float t = 0.0;
  float tol = dfactor*TOLERANCE;
  ii = MAX_RAY_MARCHES;
  for (int i = 0; i < MAX_RAY_MARCHES; ++i) {
    if (t > MAX_RAY_LENGTH) {
      t = MAX_RAY_LENGTH;    
      break;
    }
    float d = dfactor*df(ro + rd*t);
    if (d < TOLERANCE) {
      ii = i;
      break;
    }
    t += d;
  }
  return t;
}

vec3 render(vec3 ro, vec3 rd) {
  vec3 agg = vec3(0.0, 0.0, 0.0);
  vec3 ragg = vec3(1.0);

  bool isInside = df(ro) < 0.0;

  for (int bounce = 0; bounce < MAX_BOUNCES; ++bounce) {
    float dfactor = isInside ? -1.0 : 1.0;
    float mragg = min(min(ragg.x, ragg.y), ragg.z);
    if (mragg < 0.025) break;
    int iter;
    float st = rayMarch(ro, rd, dfactor, iter);
    const float mrm = 1.0/float(MAX_RAY_MARCHES);
    float ii = float(iter)*mrm;
    vec3 mat = g_mat;
    if (st >= MAX_RAY_LENGTH) {
      agg += ragg*skyColor(ro, rd);
      break; 
    }

    vec3 sp = ro+rd*st;

    vec3 sn = dfactor*normal(sp);
    float fre = 1.0+dot(rd, sn);
//    fre = clamp(abs(fre), 0.0, 1.0);
    fre *= fre;
    fre = mix(0.1, 1.0, fre);

    vec3 ld     = normalize(lightPos - sp);

    float dif   = max(dot(ld, sn), 0.0); 
    vec3 ref    = reflect(rd, sn);
    float re    = mat.z;
    float ire   = 1.0/re;
    vec3 refr   = refract(rd, sn, !isInside ? re : ire);
    vec3 rsky   = skyColor(sp, ref);
    vec3 col = vec3(0.0);    
    col += diffuseCol*dif*dif*(1.0-mat.x);
    float edge = smoothstep(1.0, 0.9, fre);
    col += rsky*mat.y*fre*vec3(1.0)*edge;
    if (isInside) {
      ragg *= exp(-st*g_beer);
    }
    agg += ragg*col;

    if (refr == vec3(0.0)) {
      rd = ref;
    } else {
      ragg *= mat.x;
      isInside = !isInside;
      rd = refr;
    }

    // TODO: if beer is active should also computer it based on initt    
    ro = sp+initt*rd;
  }

  return agg;
}

vec3 effect(vec2 p) {
  g_rot = rot_x(0.2*TIME)*rot_y(0.3*TIME);
  vec3 ro = 0.5*vec3(0.0, 2.0, 5.0);
  const vec3 la = vec3(0.0, 0.0, 0.0);
  const vec3 up = vec3(0.0, 1.0, 0.0);

  vec3 ww = normalize(la - ro);
  vec3 uu = normalize(cross(up, ww ));
  vec3 vv = normalize(cross(ww,uu));
  const float fov = tan(TAU/6.);
  vec3 rd = normalize(-p.x*uu + p.y*vv + fov*ww);

  vec3 col = render(ro, rd);

  return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1. + 2. * q;
  p.x *= RESOLUTION.x/RESOLUTION.y;
  vec3 col = vec3(0.0);
  col = effect(p);
  col = aces_approx(col); 
  col = sRGB(col);
  fragColor = vec4(col, 1.0);
}

#define AA 4

#define ROLLING_SHUTTER
#define CENTER_RADIUS 0.125
#define BLADE_RADIUS 0.25
#define BLADE_THICKNESS 0.25
#define BLADES 3.0
#define SPEED 50.0 * (iMouse.x / iResolution.x - 0.5)


const float pi = 3.1415926535;
const float tau = pi * 2.0;
const float dist = CENTER_RADIUS + BLADE_RADIUS - 0.05;


bool circle(vec2 p, vec2 center, float radius)
{
    return distance(p, center) <= radius;
}

float collides(vec2 p)
{
    // Center
    bool collision = circle(p, vec2(0), CENTER_RADIUS);
    float t = iTime;

#ifdef ROLLING_SHUTTER
    t += p.y * 0.125;
#endif

    for (float i = 0.; i < BLADES; i++)
    {
        float a = i * tau / BLADES + t * SPEED;
        vec2 cs = vec2(cos(a), sin(a));
        mat2 m = mat2(cs.x, cs.y/BLADE_THICKNESS, -cs.y, cs.x/BLADE_THICKNESS);

        // Blade
        collision = collision || circle(m * p, vec2(dist, 0), BLADE_RADIUS);
    }

    return float(collision);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float mini = min(iResolution.x, iResolution.y);
    float c = 0.0;

    // MSAA
    for (int j = 0; j < AA; j++)
        for (int i = 0; i < AA; i++)
        {
            vec2 co = floor(fragCoord);
            vec2 offset = vec2(i, j) / float(AA);
            vec2 uv = (2.0 * (co + offset) - iResolution.xy) / mini;
            c += collides(uv);
        }

    c /= float(AA * AA);
    fragColor = vec4(c);
}

// Global speed of the animation: higher means faster; default(0.15)
#define SPEED 0.15

// You can use the 'time' variable inside of these defines
// like e.g. #define OPACITY sin(time)

// How much are background layers blured: higher gives more blur; default(0.03)
#define BLUR_FACTOR 0.03
// The opacity of a layer: 0.0 means transparent, 1.0 means opaque; default(0.35)
#define OPACITY 0.35
// Layer count: how many layers are there; default(10)
#define LAYER_COUNT 10
// Horizon height: higher makes peaks higher
#define HORIZON_HEIGHT map_range(smoothstep(0.0, 0.5, abs(fract(time*0.05) - 0.5)), 0.0, 0.5, 0.2, 0.4)
// Moves everything up or down (default 0.05)
#define Y_OFFSET 0.05
// Fog: the higher, the more transparent are deeper layers; default(2.5)
#define FOG 2.5

// Background color (default vec3(0.03, 0.06, 0.19)) 
#define BACKGROUND vec3(0.03, 0.06, 0.19)
// Front Hue: the Hue of the front most layer color; default(0.45)
#define H0 0.45
// Middle Hue: the Hue of the middle layer color; default(0.53)
#define H1 0.53
// Last Hue: the Hue of the last layer color; default(0.84)
#define H2 0.84
// Saturation all layer colors; default(0.59)
#define SATURATION 0.59
// Value of all layer colors; default(1.0)
#define VALUE 1.0

// Enable MSAA; default(0)
#define MSAA 0
// Size of MSAA lookup area: larger gives a more antialiased output; (default 0.5)
#define MSAA_SIZE 0.5
// Number of MSAA samples: more gives higher quality, but slower; default(4.0)
#define MSAA_SAMPLES 4.0

float map_range(float val, float old_min, float old_max, float new_min, float new_max)
{
    return mix(new_min, new_max, (val - old_min) / (old_max - old_min));
}

float wave(vec2 UV, vec2 translate, vec2 scale, float time)
{
    return sin((UV.x + time) * scale.x + translate.x) * scale.y + translate.y;
}
float layer(vec2 UV, float depth, float time)
{
    float scaleX = depth * 0.25;
    float scaleY = 0.1 / depth;
    vec2 translate = vec2(pow(depth, 5.0) * 1.618033, (HORIZON_HEIGHT + Y_OFFSET) - HORIZON_HEIGHT / depth);

    const int SIZE = 2;
    vec2[] s = vec2[](
        vec2(5.0 * scaleX, scaleY),
        vec2(7.0 * scaleX, scaleY)
    );

    float r = 0.0;
    for (int i = 0; i < SIZE; ++i) {
        r += wave(UV, translate, s[i], time * 0.1 + time * 0.1 * float(i + 1));
    }    

    float x = UV.y - r;
    float f = (depth - 1.0) * BLUR_FACTOR;
    float a = smoothstep(0.0, 1.0, clamp(-x / f + 0.5, 0.0, 1.0));

    return float(a) * OPACITY;
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

float map(int i, int n)
{
    float x = float(i) / float(n);
    return clamp(pow(x, 0.3) - 0.1, 0.0, 1.0);
}

vec3 color(float x, float time)
{
    float h;
    if (x <= 0.5)
        h = mix(H0, H1, smoothstep(0.0, 0.5, x));
    else
        h = mix(H1, H2, smoothstep(0.5, 1.0, x));
    return hsv2rgb(vec3(h, SATURATION, VALUE));
}

vec3 calculate(vec2 UV, float time)
{
    vec3 r = BACKGROUND;
    for (int i = LAYER_COUNT - 1; i >= 0; --i)
    {
        float opacity = max(layer(UV, float(i), time), 0.0);
        opacity *= pow((1.0 - float(i) / float(LAYER_COUNT)), FOG);
        r = opacity * color(map(i, LAYER_COUNT), time) + (1.0 - opacity) * r;
    }
    return r;
}

