    float samples = 0.0;
    for (float x = fragCoord.x - MSAA_SIZE; x <= fragCoord.x + MSAA_SIZE; x += 2.0 * MSAA_SIZE / MSAA_SAMPLES) {
    for (float y = fragCoord.y - MSAA_SIZE; y <= fragCoord.y + MSAA_SIZE; y += 2.0 * MSAA_SIZE / MSAA_SAMPLES) {
        r += calculate(vec2(x, y)/iResolution.xy, time);
        samples += 1.0;
    }}
    r /= samples;
#else
    vec3 r = calculate(fragCoord/iResolution.xy, time);
#endif
    fragColor = vec4(r, 1.0);
}

