const float PI = 3.141592653589793;

float SPHKernel (float x)
{
    if (x < 1.0)
        return 4.0 * cos(x*PI) + cos((x + x) * PI) + 3.0;
    else
        return 0.0;
}

float SPHgradKernel (float x)
{
    if (x < 4.0)
    {
        float xx = x*x;
        float xxx = xx*x;
        float xxxx = xxx*x;
        return 0.000 + 3.333 * x + -3.167 * xx + 0.917 * xxx + -0.083 * xxxx;
    }
    else
        return 0.0;
}

float length2(vec2 v)
{
    return dot(v, v);
}

float square(float x)
{
    return x * x;
}

#define keyClick(ascii)   ( texelFetch(iChannel3,ivec2(ascii,1),0).x > 0.)
#define keyDown(ascii)    ( texelFetch(iChannel3,ivec2(ascii,0),0).x > 0.)

void insertion_sort(inout ivec4 i, inout vec4 d, int i_, float d_){	
    if(any(equal(ivec4(i_),i))) return;
    if     (d_ < d[0])             
        i = ivec4(i_,i.xyz),    d = vec4(d_,d.xyz);
    else if(d_ < d[1])             
        i = ivec4(i.x,i_,i.yz), d = vec4(d.x,d_,d.yz);
    else if(d_ < d[2])            
        i = ivec4(i.xy,i_,i.z), d = vec4(d.xy,d_,d.z);
    else if(d_ < d[3])           
        i = ivec4(i.xyz,i_),    d = vec4(d.xyz,d_);
}

#define r(a) mat2(cos(vec4(0,11,33,0)+ a))

float kaplanHingedSquares( vec2 u, float time, vec2 res )
{
    vec2 R = res;
    u = 2.0 * u/R.y - vec2(R.x/R.y, 1.);

    float c, i, a = modf(time * .5, i) * .78; // PI / 4

    u += vec2(-.1 - sin(a * 4.) * .04, .1) + sin(a + a) * .1;
    u *= r(-a);

    c = abs(sin(a + a)) / 12.5 + .2;

    u = mod(u, c+c);

    a = u.x > c ^^ u.y > c ? -a : a;

    u = mod(u, c);

    vec2 d = abs(r(-a) * (u - c*.5)) - .1;

    return clamp(max(0., abs(length(max(d,0.)) + min(max(d.x,d.y),0.)) - .004) * R.y * .5, 0., 1.);
}


#define C(i) vec4[](vec4(1, .6, .2, 1),vec4(.6, .2, .7,1),vec4(.9, .2, .2, 1),vec4(.3, .6, .1, 1),vec4(0, .6, .5, 1))[(i) % 5]

vec3 kaplanFiveColoredSquares( vec2 u, float time, vec2 res )
{
    vec4 O;
    vec2 R = 1./res;
    float i, a = modf(time * .5, i) * .78; // .78 = PI / 4

    u = (2./R.xy - 4.*u) * R.y * (mod(i, 2.) - .5) + vec2(sin(a * 4.) * .04 + .1, -.1) - sin(a + a) * .1;

    mat2 M = mat2(cos(vec4(0,11,33,0) - a));
    u *= M;

    float c = abs(sin(a + a)) * .08 + .2,
          m = cos(a)*.2,
          n = sin(a)*.2;

    vec2 D = mod(u / (c+c), 5.),
         q = u = mod(u, c+c),
         b = mod(u, c) - c * .5;

    O = mix(
        ((q.y < m || q.y > c+c - m) && (q.x < n || q.x > c+c - n)) ?
            C(int(D.y + 3.5) + int(D.x + 2.5) * 3) :
        (q.y - c > -m && q.y - c < m && q.x - c > -n && q.x - c < n) ?
            C((int(7. - D.y) * 2 + int(5. - D.x) + 2) * 2) :
        (q.x - c > -m && q.x - c < m) && (q.y < n || q.y > c+c - n) ?
            C(int(D.y + 2.5) + int(D.x + 2.) * 3) :
        ((q.x < m || q.x > c+c - m) && (q.y - c > -n && q.y - c < n)) ?
            C((int(8. - D.y) * 2 + int(7.5 - D.x)) * 2) : C(1),

        C(int(mod(D.x*-2., 5.)) + 3 * int(mod(D.y+D.y, 5.)) + 1),
               smoothstep(-R.y, R.y,
               -length(max(q = abs(u.x > c ^^ u.y > c ? b * M : M * b) - .1,0.)) - min(max(q.x,q.y),0.)));

    return clamp(O.xyz * O.xyz, 0., 1.);
}

