// https://en.wikipedia.org/wiki/Signed_distance_function
float paraboloidZSDF(vec3 p, float a, float b) {
    return (pow(p.x, 2.)/pow(a, 2.)) + (pow(p.y, 2.)/pow(b, 2.)) - p.z;
}

