#define C(p,r) ( r - length(p) )                             // circle SDF
#define B(p,r) ( r - max(abs(p).x,abs(p).y) )                // box SDF
#define D(c,v) l = c, l < d ? d=l, b=v : v  

#define T iTime

float SDF(vec2 p, out float b ) {                            // scene SDF. out = border id or value
    float d = 1e4, l; 
          D( B(p, 1.) , 1. );
          D( -C(p - 1., .4) , 1. );
          D( -C(p - vec2(-1, 1), .7) , 1. );
          D( -C(p - vec2( 1,-1), .5), 1. );
          D( -C(p +  1., .8), 1. );
          D( -C(p+.3*vec2(cos(T),sin(1.3*T)), .5), 0. );
    return   d;
}

void mainImage(out vec4 O, vec2 u) {
    vec2  R = iResolution.xy, s,
          p = ( u+u - R ) / R.y; 
    float v = 0., j,i = v, b,  N = 256.;

    if ( SDF(p,b) < -.05 ) { O-=O; return; }

    for ( ; i++ < N                                          // diffusion = averaging N random walks
          ; v += b                                           // boundary condition
        ) 
        for ( s = p, j = 0.; j++ < 8.; )                     // random walk from sphere marching ( S steps )
            s += SDF(s,b) * cos( 6.28* fract(sin( dot(s,R-17.) + (i-T)*269. ) *4e4 ) // CosSin( 2pi*hash(s) )
                               + vec2(0,11));

    O =  vec4(  v/N , SDF(p,b) < 0. ? 0. : v/N, 0, SDF(p,b) > 0.);
}

