//uses the algorithm explained below to get the signed distance to a line segment
//a = line start, b = line end, r = radius of line, p = point to get distance
float sdLine(vec2 a, vec2 b, float r, vec2 p){
    vec2 ab = b - a;
    float t = dot(p - a, ab) / dot(ab, ab);
    vec2 p2 = a + clamp(t,0.0,1.0) * ab;
    return length(p - p2) - r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //normalized uv coords
    vec2 uv = fragCoord/iResolution.y;
    //normalized mouse click coords
    vec2 m = abs(iMouse.zw)/iResolution.y;
    if (m == vec2(0,0)) {
        m = vec2(0.7,0.4);
    }

    vec2 a = vec2(0.5,0.5); //line segment start point
    vec2 b = vec2(0.8,0.7); //line segment end point
    vec2 p = m; //point to get distance of

    vec2 ab = b-a; //the vector from point a to point b
    vec2 ap = p-a; //the vector from point a to point p
    float t = dot(ap,ab)/dot(ab,ab); //vector ap projected onto vector ab
    vec2 p2 = a + clamp(t, 0.0, 1.0)*ab; //the projected point on ab, with t clamped so that p2 stays on the segment

    float d = distance(p, p2); //the distance from the point to the line segment

    //lines used to show example
    vec3 col = sdLine(a, b, 0.01, uv) < 0.0 ? vec3(1) : vec3(0);
    col = sdLine(a, p, 0.004, uv) < 0.0 ? vec3(0.8) : col;
    col = sdLine(p, p2, 0.002, uv) < 0.0 ? vec3(0.6) : col;
    col = sdLine(a, p2, 0.006, uv) < 0.0 ? vec3(0,1,0) : col;
    col = distance(p,uv) < 0.015 ? vec3(0,0,1) : col;

    // Output to screen
    fragColor = vec4(col,1.0);
}

const vec3 light = vec3(-0.3,-0.6,-0.3);
const vec3 lightCol = vec3(1,1,0.9);

//a sdf circle
float sdCircle(vec2 uv, float r){
    return length(uv)-r;
}

//returns the normal of a sdf circle
vec3 sdCircleNormal(vec2 uv){
    vec2 base = normalize(uv)*sqrt(6.0)/3.0;
    return vec3(base.x,sqrt(3.0)/3.0,base.y);
}

//a sdf line
float sdLine(vec2 a, vec2 b, float r, vec2 p){
    vec2 ab = b - a;
    float t = dot(p - a, ab) / dot(ab, ab);
    vec2 p2 = a + clamp(t,0.0,1.0) * ab;
    return length(p - p2) - r;
}

//returns the normal of a sdf line
vec3 sdLineNormal(vec2 a, vec2 b, vec2 p){
    vec2 ab = b - a;
    float t = dot(p - a, ab) / dot(ab, ab);
    vec2 p2 = a + clamp(t,0.0,1.0) * ab;
    vec2 base = normalize(p-p2)*sqrt(6.0)/3.0;
    return vec3(base.x,sqrt(3.0)/3.0,base.y);
}

//given a radius and distance value, warps the distance to be circular
float roundSdf(float r, float d){
    if (d < -r) return -r;
    float b = d*d + 2.0*d*r;
    return sign(b)*sqrt(abs(b));
}

//given a radius, distance, and normal, bends the normal to add a circular bevel
vec3 roundNormal(float r, float d, vec3 normal){
    if (d < -r) return vec3(0,1,0);
    float b = d*d + 2.0*d*r;
    float m = (d+r)/sqrt(abs(b));
    float l = sqrt(d*d*(m*m + 1.0));
    vec2 v = sign(d)*vec2(-m*d,d)/l;
    vec2 a = normalize(normal.xz)*v.x;
    vec3 x = vec3(-a.y,v.y,-a.x);
    return x/length(x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy/2.0)/iResolution.y;

    float circleDist = sdCircle(uv, 0.2);
    float lineDist = sdLine(vec2(-0.2,0.3),vec2(0.2,0.3),0.03,uv);
    float dist = 0.0;
    vec3 normal = vec3(0,1,0);
    if (lineDist < circleDist){
        dist = lineDist;
        //get normal of line and bend it
        normal = sdLineNormal(vec2(-0.2,0.3),vec2(0.2,0.3),uv);
        normal = roundNormal(0.03, dist, normal);
    } else {
        dist = circleDist;
        //get normal of circle and bend it
        normal = sdCircleNormal(uv);
        normal = roundNormal(0.2, dist, normal);
    }
    //base color
    vec3 col = dist < 0.0 ? vec3(0.3,0.4,0.8) : vec3(0.8,0.4,0.2);
    //lighting
    float diffuse = clamp(dot(normal,normalize(-light)),0.0,1.0);
    vec3 specularAngle = reflect(vec3(0,-1,0),normal);
    float specular = max(dot(specularAngle,normalize(-light)),0.0);
    float ambient = 0.2;
    col = col*diffuse + col*ambient + lightCol*pow(specular,10.0)*0.5;
    fragColor = vec4(col,1.0);
}

