float saturate( float x ) { return clamp( x, 0.0, 1.0 ); }
vec3 magma_quintic( float x ) {
	x = saturate( x );
	vec4 x1 = vec4( 1.0, x, x * x, x * x * x ); // 1 x x2 x3
	vec4 x2 = x1 * x1.w * x; // x4 x5 x6 x7
	return vec3(
