		dot( x1.xyzw, vec4( +0.010680993, +0.176613780, +1.638227448, -6.743522237 ) ) + dot( x2.xy, vec2( +11.426396979, -5.523236379 ) ),
		dot( x1.xyzw, vec4( -0.008260782, +2.244286052, +3.005587601, -24.279769818 ) ) + dot( x2.xy, vec2( +32.484310068, -12.688259703 ) ) );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    float interSpace3 = interSpace1*interSpace2;

    vec2 uv = fragCoord/iResolution.xy;

    float maxHeight = 0.;
    int   maxIndex  = 0;

    for (int i = 0; i <= int(interSpace2); i++) {

        float val = texture( iChannel0, uv ).r;

        if (val > maxHeight ) {
            maxHeight = val;
            maxIndex = i;
        }
        uv.y += 1./iResolution.y;
    }

    float finalHeight = max(maxHeight - float(maxIndex)/interSpace3/slices,0.);

    float wall = float(maxIndex>0);

    float finalVal = pow(finalHeight,0.75)*(1.-wall*0.45);

    fragColor = vec4(magma_quintic(finalVal), 1.0);

}

