float getVal( vec2 fc, vec2 res ) {
    vec2 offset = -iTime*vec2(0.05,0.03);

    vec2 uv = (fc - res*offset)/mapSize;
    uv.y *= tilt;

    return sqrt(texture( iChannel0, uv ).r)
     *(texture( iChannel0, uv/sqrt(2.)*0.6 ).r);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 fc = fragCoord;

    float interSpace3 = interSpace1*interSpace2;

    float maxHeight = 0.;

    int i = int(slices);
    while (maxHeight == 0. && i > 0) {

        float val = getVal(fc,iResolution.xy);

        float currentSlice = float(i) / slices;

        if (val >= currentSlice) {
            maxHeight = currentSlice;
            break;
        }

        fc.y += interSpace3;
        i--;
    }

    fragColor = vec4(maxHeight,0.,0.,1.);
}

