  float back = mix(0.45, 0.55, rand);
  float cut = 0.99;
  back = rand > cut ? xclampnorm(rand, cut, 1.0) : back;

  float midRaw = flatten(nturb2Simplex(uv*3., seed), 2.0);
  float mid = mix(-0.02, 0.02, midRaw);

