  float back2a = cel1.x;
  back2a = rsat(back2a*30.);
  back2a = flatten(back2a, 7.0);
  float back2Raw = back2a;
  float back2 = denorm(back2Raw)*0.06 + 0.02;
  back2 += xclampnorm(back2Raw, 0.0, 0.2)*0.2;
  back2 -= xclamp(back2Raw, 0.9, 1.0)*0.1;

  float final = back + mid + back2;

  v1 = back2Raw;
  v2 = b2uvTurb1;
  v3 = midRaw;
  v = final;//final;

  return vec4(v1, v2, v3, v);
}


