vec3 rgb2hsv(vec3 c)
{
    vec4 P = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = c.g < c.b ? vec4(c.bg, P.wz) : vec4(c.gb, P.xy);
    vec4 q = c.r < p.x ? vec4(p.xyw, c.r) : vec4(c.r, p.yzx);
    // vec4 p = mix(vec4(c.bg, P.wz), vec4(c.gb, P.xy), step(c.b, c.g));
    // vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 P = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + P.xyz) * 6.0 - P.www);
    return c.z * mix(P.xxx, clamp(p - P.xxx, 0.0, 1.0), c.y);
}
// -------------------
// END HSV Conversion 
// -------------------

vec3 hsv(float h, float s, float v) {return hsv2rgb(vec3(h, s, v));}


// Noise Aux Functions
float random(vec2 uv, float seed) {
    const float a = 12.9898;
    const float b = 78.233;
    const float c = 43758.543123;
    return fract(sin(dot(uv, vec2(a, b)) + seed) * c);
}

#define OCTAVES 6
#define OCTAVES_F 6.0

// Simplex Variations
#define SIMPLEX_VARS_SCALE 1.0
float turbSimplex( vec3 p ) {
  p *= SIMPLEX_VARS_SCALE;
	float w = 100.0;
	float t = -.5;
  vec3 shift = vec3(100.);

	for (float f = 1.0 ; f < OCTAVES_F ; f++ ){
		float power = pow( 2.0, f );
		t += abs( simplex( power * p + shift ) / power );
	}

	return t;
}

float nturbSimplex(vec2 p, float seed) {
  p *= 2.;
  seed *= 0.08;
  return 1.-sat(-1.2*turbSimplex(vec3(p, seed)));
}

float nturb2Simplex(vec2 st2, float seed) {
  seed *= 0.04;
  vec3 st = vec3(st2, seed);
  st *= SIMPLEX_VARS_SCALE * 3.;
  float value = 0.0;
  float amplitude = 1.0;
  vec3 shift = vec3(200.);
  for (int i = 0; i < OCTAVES; i++) {
      value += amplitude * abs(simplex(st));
      st = st * 2. + shift;
      amplitude *= .5;
  }
  return clamp(value, -1., 1.);
}

float fbmSimplex(vec3 x) {
  x *= SIMPLEX_VARS_SCALE;
	float v = 0.0;
	float a = 1.;
	vec3 shift = vec3(300.);
	for (int i = 0; i < OCTAVES; ++i) {
		v += a * simplex(x);
		x = x * 2.0 + shift;
		a *= 0.5;
	}
	return clamp(v, -1., 1.);
}

float nfbmSimplex(vec2 x, float seed) {
  x *= 5.;
  seed *= 0.2;
  return norm(fbmSimplex(vec3(x, seed)));
}


// Morgan Variations
#define MORGAN_VARS_SCALE 2.5
float turbMorgan( vec3 p ) {
  p *= MORGAN_VARS_SCALE;
	float w = 100.0;
	float t = -.5;
  vec3 shift = vec3(0.);

	for (float f = 1.0 ; f < OCTAVES_F ; f++ ){
		float power = pow( 2.0, f );
		t += abs( denorm(morgan( power * p + shift )) / power );
	}

	return t;
}

float nturbMorgan(vec2 p, float seed) {
  p *= 2.;
  seed *= 0.05;
  return 1.-sat(-2.0*turbMorgan(vec3(p, seed)));
}

float nturb2Morgan(vec2 st2, float seed) {
  seed *= 0.03;
  vec3 st = vec3(st2, seed);
  st *= MORGAN_VARS_SCALE * 6.;
  float value = 0.0;
  float amplitude = 1.0;
  vec3 shift = vec3(0.);
  for (int i = 0; i < OCTAVES; i++) {
      value += amplitude * abs(denorm(morgan(st)));
      st = st * 2. + shift;
      amplitude *= .5;
  }
  return clamp(value, -1., 1.);
}

float fbmMorgan(vec3 x) {
  x *= MORGAN_VARS_SCALE;
	float v = 0.0;
	float a = .9;
	vec3 shift = vec3(0.);
	for (int i = 0; i < OCTAVES; ++i) {
		v += a * (denorm(morgan(x))+0.05);
		x = x * 2.0 + shift;
		a *= 0.5;
	}
	return clamp(v, -1., 1.);
}

float nfbmMorgan(vec2 x, float seed) {
  x *= 5.;
  seed *= 0.2;
  return norm(fbmMorgan(vec3(x, seed)));
}



// Cellular Variations
#define CELLULAR_VARS_SCALE 1.5
float turbCellular( vec3 p ) {
  p *= CELLULAR_VARS_SCALE;
	float w = 100.0;
	float t = -.5;
  vec3 shift = vec3(700.);

	for (float f = 1.0 ; f < OCTAVES_F ; f++ ){
		float power = pow( 2.0, f );
		t += abs( denorm(cellular( power * p + shift ).x) / power );
	}

	return t;
}

float nturbCellular(vec2 p, float seed) {
  p *= 2.;
  seed *= 0.07;
  return sat(0.85+2.*turbCellular(vec3(p, seed)));
}

float nturb2Cellular(vec2 st2, float seed) {
  seed *= 0.02;
  vec3 st = vec3(st2, seed);
  st *= CELLULAR_VARS_SCALE * 5.;
  float value = 0.0;
  float amplitude = 1.0;
  vec3 shift = vec3(800.);
  for (int i = 0; i < OCTAVES; i++) {
      value += amplitude * abs(denorm(cellular(st).x));
      st = st * 2. + shift;
      amplitude *= .5;
  }
  return clamp(value, -1., 1.);
}

float fbmCellular(vec3 x) {
  x *= CELLULAR_VARS_SCALE;
	float v = 0.0;
	float a = 1.;
	vec3 shift = vec3(900.);
	for (int i = 0; i < OCTAVES; ++i) {
		v += a * denorm(cellular(x).x);
		x = x * 2.0 + shift;
		a *= 0.5;
	}
	return clamp(v, -1., 1.);
}

float nfbmCellular(vec2 x, float seed) {
  seed *= 0.1;
  x *= 5.;
  return norm(fbmCellular(vec3(x, seed)));
}


vec2 rotate(vec2 v, float angle) {
  float s = sin(angle);
  float c = cos(angle);
  return mat2(c, -s, s, c) * v;
}

#define RED vec3(1., 0., 0.)
#define GRE vec3(0., 1., 0.)
#define BLU vec3(0., 0., 1.)
#define BLU2 vec3(0.35, 0.5, 1.)
#define PUR vec3(1., 0., 1.)
#define YEL vec3(1., 1., 0.)
#define CYA vec3(0., 1., 1.)
#define WHI vec3(1., 1., 1.)
#define BLANK vec3(0.35, 0., 0.35)


vec3 v2layers(float v) {
  // below
  float vup = 1.;
  float vhi = 0.65;
  float r0 = -0.5;
  vec3 c1b = vec3(0, 0, vhi);
  vec3 c1e = vec3(0, 0, vup);
  float r1 = 0.0;

  // very low
  vec3 c2b = vec3(0, vhi, vhi);
  vec3 c2e = vec3(0, vup, vup);
  float r2 = 0.1;

  // low
  vec3 c3b = vec3(vhi*0.7, 0, vhi);
  vec3 c3e = vec3(vup*0.7, 0, vup);
  float r3 = 0.3;

  // midlow
  vec3 c4b = vec3(vhi, vhi, vhi)*0.35;
  vec3 c4e = vec3(vup, vup, vup)*0.35;
  float r4 = 0.5;

  // midhigh
  vec3 c5b = vec3(vhi, vhi, vhi);
  vec3 c5e = vec3(vup, vup, vup);
  float r5 = 0.7;

  // high
  vec3 c6b = vec3(vhi, vhi, 0);
  vec3 c6e = vec3(vup, vup, 0);
  float r6 = 0.9;

  // very high
  vec3 c7b = vec3(vhi, vhi*0.7, 0);
  vec3 c7e = vec3(vup, vup*0.7, 0);
  float r7 = 1.00001;

  // above
  vec3 c8b = vec3(vhi, 0, 0);
  vec3 c8e = vec3(vup, 0, 0);
  float r8 = 1.5;

  vec3 c;
  c += xstep(r0, r1, v) * mix(c1b, c1e, (v-r0)/(r1-r0));
  c += xstep(r1, r2, v) * mix(c2b, c2e, (v-r1)/(r2-r1));
  c += xstep(r2, r3, v) * mix(c3b, c3e, (v-r2)/(r3-r2));
  c += xstep(r3, r4, v) * mix(c4b, c4e, (v-r3)/(r4-r3));
  c += xstep(r4, r5, v) * mix(c5b, c5e, (v-r4)/(r5-r4));
  c += xstep(r5, r6, v) * mix(c6b, c6e, (v-r5)/(r6-r5));
  c += xstep(r6, r7, v) * mix(c7b, c7e, (v-r6)/(r7-r6));
  c += xstep(r7, r8, v) * mix(c8b, c8e, (v-r7)/(r8-r7));
  return c;
}

float getNoise(vec2 uv, float seed, float t, int inx) {
  vec2 roll = -0.02*t*vec2(1.);
  uv += roll;
  float vs[3];
  float vRand = random(uv, seed);
  //float vTurbSimplex = nturbSimplex(uv, seed);
  //float vTurb2Simplex = nturb2Simplex(uv, seed);
  //float vFbmSimplex = nfbmSimplex(uv, seed);
  //float vTurbMorgan = nturbMorgan(uv, seed);
  float vTurb2Morgan = nturb2Morgan(uv, seed);
  //float vFbmMorgan = nfbmMorgan(uv, seed);
  //float vTurbCellular = nturbCellular(uv, seed);
  //float vTurb2Cellular = nturb2Cellular(uv, seed);
  //float vFbmCellular = nfbmCellular(uv, seed);
  //float vMorgan = nmorgan(uv, seed);
  float vSimplex = nsimplex(uv, seed);
  //float vPerlin = nperlin(uv, seed);
  vec2 cel = ncellular(uv, seed);
  float vCelX = cel.x;
  //float vCelY = cel.y;
  //float vCelAvg = (cel.x + cel.y)*.5;

  vs[0] = vCelX;
  vs[1] = vSimplex;
  vs[2] = vTurb2Morgan;
#ifndef MIXES
  vs[0] = flatten(vs[0], 5.0);
  vs[1] = flatten(vs[1], 5.0);
  vs[2] = flatten(vs[2], 5.0);
#endif
  return vs[inx];
}

struct st_value {
  float v;
  float x0_2;
  float x0_5;
  float x1_2;
  float x2;
  float x5;
  float x10;
  float x20;
};

st_value buildValues(vec2 uv, float seed, float t, int inx) {
  st_value v;
  v.v = getNoise(uv, seed, t, inx);
  v.x0_2 = getNoise(uv*0.2, seed, t, inx);
  v.x0_5 = getNoise(uv*0.5, seed, t, inx);
  v.x1_2 = getNoise(uv*1.2, seed, t, inx);
  v.x2 = getNoise(uv*2., seed, t, inx);
  v.x5 = getNoise(uv*5., seed, t, inx);
  v.x10 = getNoise(uv*10., seed, t, inx);
  v.x20 = getNoise(uv*20., seed, t, inx);
  //v.x30 = getNoise(uv*30., seed, t, inx);
  //v.x100 = getNoise(uv*100., seed, t, inx);
  return v;
}

void drawNoiseDescRows(inout vec3[100] sims, inout int count, inout st_value v) {
  // row 1
  sims[count++] = v2layers(v.v);
  sims[count++] = WHI * v.v;
  sims[count++] = GRE * max(v.v, v.x10);
  sims[count++] = RED * min(v.v, v.x10);
  sims[count++] = CYA * flatten(v.v, 10.0);
  sims[count++] = CYA * flatten(v.v, 3.0);
  sims[count++] = YEL * flatten(xclampnorm(v.v, 0.4, 0.6), 2.0);
  sims[count++] = GRE * xclampnorm(v.v, .0, 0.5);
  sims[count++] = GRE * xclampnorm(v.v, 1./3., 2./3.);

  // row 2
  sims[count++] = hsv(v.v, 1., 1.);
  sims[count++] = WHI * v.x5;
  sims[count++] = WHI * v.x20;
  sims[count++] = PUR * nsin(v.v);
  sims[count++] = PUR * ncos(v.v);
  sims[count++] = PUR * ntan(v.v);
  sims[count++] = BLU * (v.v/v.x2)/2.;
  sims[count++] = BLU * (v.x10/v.v)/2.;
  sims[count++] = CYA * (v.x5+v.x10)/2.;

  // row 3
  sims[count++] = hsv(rsat(v.v*6.), 1., 1.);
  sims[count++] = GRE * rsat(v.v*2.);
  sims[count++] = GRE * rsat(v.x5*2.);
  sims[count++] = GRE * rsat(v.v*10.);

  sims[count++] = RED * rsat(1.-v.x5);

  sims[count++] = BLU * rsat(1./v.v);
  sims[count++] = BLU * rsat(1./v.x5);
  sims[count++] = PUR * rsat(v.v-nsin(v.v));
  sims[count++] = PUR * rsat(v.v+ntan(v.v));
}

void drawBlendDescRows(inout vec3[100] sims, inout int count,
  float t, st_value v1, st_value v2) {
  st_value va = v1;
  st_value vb = v2;
  int invert = int(t*0.1) % 2;
  if (invert == 1) {
    v1 = vb;
    v2 = va;
  }

  // row 1
  sims[count++] = v2layers((v1.v+v2.v)/2.);
  sims[count++] = v2layers(rsat(v1.v+v2.v));
  sims[count++] = hsv(rsat(v1.v+v2.v), 1., 1.);
  sims[count++] = hsv(rsat((v1.v+v2.v)*3.), 1., 1.);
  sims[count++] = vec3(v1.v, 0., v2.v);
  sims[count++] = vec3((v1.v+v2.v)/2., v1.v, rsat(v2.v*5.));
  sims[count++] = vec3(1.-v1.v, v1.v, rsat(v2.v*5.));
  sims[count++] = BLANK;
  sims[count++] = BLANK;

  // row 2
  sims[count++] = WHI * max(v1.v, v2.v);
  sims[count++] = WHI * min(v1.v, v2.v);
  sims[count++] = WHI * xclamp(v2.v, v2.x10, v1.v);
  sims[count++] = WHI * xclamp(v2.v, v2.x2, v1.x10);
  sims[count++] = WHI * (xclamp(v1.v, .0, .5) + xclamp(v2.v, .5, 1.));
  sims[count++] = WHI * (xclampnorm(v1.v, .0, .5) + xclampnorm(v2.v, .5, 1.));
  sims[count++] = WHI * flatten(
    max(xclampnorm(v1.v, .45, .55), xclampnorm(v2.v, .45, .55))
  , 2.0);
  sims[count++] = BLANK;

  // row 3
  sims[count++] = GRE * rsat(v1.v + v2.v);
  sims[count++] = GRE * rsat(v1.v + v2.x5);
  sims[count++] = RED * rsat(v1.v - v2.v);
  sims[count++] = RED * rsat(v1.x5-v2.x10);
  sims[count++] = BLU * (v2.x10/v1.v)/2.;
  sims[count++] = BLU * (v1.v/v2.x10)/2.;
  sims[count++] = BLU * rsat(v1.v / v2.v);
  sims[count++] = BLU * rsat(1./ v1.v / v2.v);
}


#ifndef SHADERTOY
uniform float iTime;
uniform vec2 iResolution2D;
#define iResolution vec4(iResolution2D, 0., 0.)
out vec4 outColor;
