#define thc(a,b) tanh(a*cos(b))/tanh(a)
#define pi 3.14159

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec2 ms = (iMouse.xy - 0.5 * iResolution.xy) / iResolution.y;

    float mx = 19. * (0.5 - 0.5 * thc(3., 0.25 * iTime));
    ms.x = 0.52 * thc(6., iTime + mx * uv.x) * iResolution.x / iResolution.y;
    ms.y = 0.52 * thc(4., 1.5 * iTime + mx * uv.y);

    float a = 0.5 * atan(uv.x - ms.x, uv.y - ms.y);

    vec3 col = vec3(cos(a + 0.25 * iTime), 
                    sin(uv.y), 
                    sin(a + 0.25 * iTime));
    col = sqrt(0.5 + 0.5 * col);

    float d = length(uv);
    float k = 1. / iResolution.y;
    float s = smoothstep(-k, k, -d + 0.2 * (0.6 + 0.4 * cos(iTime + a)));

    col = mix(col, 0.8 * col * col, s);

    fragColor = vec4(col,1.0);
}

#define spec(a,b,c)  min( max(a,b) ,c )
#define alt(a,b,c)   max( min(a,b) ,c )

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, U = u/R;
    float a,b,c,v = fract(2.*U.x);
    U.x < .5 ?  ( a = v,  b = .3, c = .7 )
             :  ( a = .3, b = .7, c = v );

    v = U.y > .667 ?  clamp(a,b,c)
      : U.y > .333 ?   spec(a,b,c) // = clamp according to spec  https://registry.khronos.org/OpenGL-Refpages/gl4/html/clamp.xhtml
      :                 alt(a,b,c);

    O = vec4( sin(476.5 * v ) );

    if ( int(u.y)%int(R.y/3.) < 1 || int(u.x)%int(R.x/2.) < 1 ) O = vec4(1,0,0,0); // red separators
}

