float pp = 1.;
vec2 m(vec3 p){
  float d,id;
  vec3 ppp = p;
  p.x += sin(p.z*smoothstep(1.21, -1.25,nois))*.25+.25;
  vec3 p2 = p;
  p2.x = atan(p.y,p.x)*20./3.141592;
  p2.x -= p.z*.1+t*16.;
  p2.y = length(p.xy)-19.;
  float s = 5.;
  p2.z += t*26.75;
  p2 = (fract(p2/s-.5)-.5)*s;
  d = length(p2)-.125-nois*.0625;
  pp += 1./(1.+d*d)*nois;
  fin;
}

vec3 nm(vec3 p){
  vec2 e= vec2(0.01,0.);
  return normalize(m(p).x-vec3(m(p-e.xyy).x, m(p-e.yxy).x, m(p-e.yyx).x));
}

void ray(inout vec3 p, inout vec3 r, inout vec3 co){
