    uv /= vec2(iResolution.y / iResolution.x, 1.);

  vec3 p = s;
  vec3 tg = vec3(0.01+sin(t+pi)*.15, 0.01+sin(t+pi)*.16, 0.01);
  vec3 cz = normalize(tg-s);
  vec3 cx = normalize(cross(cz,vec3(0., -1., 0.)));
  vec3 cy = normalize(cross(cz,cx));
  vec3 r = mat3(cx,cy,cz)*normalize(vec3(uv, 1.+c(t)*10.));
  vec3 co = vec3(0.165);

  ray(p,r,co);
  co += pp*vec3(.23,.45,.56);
  co = pow(co, vec3(.3435));
  co *= 1.-length(p)*.005;
	fragColor = vec4(co, 1.);
}

