// Sky color made by @nimitz from https://www.shadertoy.com/view/wl3czN
#define atmoDepth 8228.
#define earthRadius 6371000.
#define mieDepth 1800
#define sunColor vec3( .95, 0.96, 1.2 )
#define ozoneHeight 30000.
#define ozoneCoefficient (vec3(3.426, 8.298, .356) * 6e-5 / 100.)
#define mieCoefficient 16e-6 // adjust for foggier look

// custom
#define rayleighCoefficient vec3(0.0000056  , 0.0000125 , 0.000029)

vec3 getThickness(vec3 rd)
{
    const vec4 cns = earthRadius + 
        			vec4(atmoDepth, mieDepth, ozoneHeight, ozoneHeight + atmoDepth);
    const float r2 = earthRadius * earthRadius;
    float b = -rd.y*earthRadius;
    vec4 z = sqrt( cns*cns + (b*b - r2) );
    return vec3(b + z.xy, z.w - z.z);
}

#define addDepth 0.0

vec4 skyTex(vec3 rd, vec3 lgt)
{
    const vec3 addCol = vec3(1.,1.,1.);
    const mat3 coeffs = mat3(rayleighCoefficient, vec3(mieCoefficient), ozoneCoefficient)/0.693147;
    vec3 thickness = getThickness(rd) + addDepth*addCol;
    float rdl = max(dot(rd, lgt), 0.0);

    vec3 rayleighScatter =(thickness.x * (0.4*rdl*rdl + 1.12))* rayleighCoefficient;
    float g = 0.8 - lgt.y*0.15 - mieCoefficient*1500.;
    float g2 = g*g;
    float a = inversesqrt(g2 - (2.0*g*rdl - 1.0));
    float phaseMie = (0.4 + lgt.y*0.1 + mieCoefficient*7000.)*(1.0 - g2)*a*a*a;
    float mieScatter = thickness.y * phaseMie * mieCoefficient;

    vec3 sunCoeff = coeffs*(getThickness(lgt) + addDepth*addCol);
    vec3 viewCoeff = coeffs*thickness;
    vec3 absorption = (exp2(-viewCoeff) - exp2(-sunCoeff))/((sunCoeff - viewCoeff)*0.693147);

    vec3 c = clamp(sunColor*(rayleighScatter + mieScatter)*absorption*(0.6 + lgt.y*0.3), 0., 100.5);

    vec3 sun = vec3(100.0, 50.0, 0.2) * smoothstep(1.-0.008726646, 1., 1.-clamp(acos(dot(rd, lgt)), 0.001, 1.0));

    c +=  sun * smoothstep(0.0, 0.2, lgt.y);

    return vec4(c, sunCoeff);
}

vec2 hash22(vec2 p)
{
    vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

float hash11(float p)
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

float erp(float x, float k)
{
    return x < 0.5 ? pow(2.0*x, k)*0.5  : 1. - pow(2.0*(1.0-x), k) / 2.;
}

mat2 rotMat(float r){ return  mat2(cos(r), -sin(r), sin(r), cos(r)); }


vec3 rotX(vec3 p, float r)
{
    return vec3(p.x, p.yz * rotMat(r));
}

vec3 rotY(vec3 p, float r)
{
    vec2 t = p.xz * rotMat(r);
    return vec3(t.x, p.y, t.y);
}

vec3 rotZ(vec3 p, float r)
{
    return vec3(p.xy * rotMat(r), p.z);
}

vec3 aces_tonemap(vec3 color) // https://www.shadertoy.com/view/XsGfWV
{	
	mat3 m1 = mat3(
        0.59719, 0.07600, 0.02840,
        0.35458, 0.90834, 0.13383,
        0.04823, 0.01566, 0.83777
	);
	mat3 m2 = mat3(
        1.60475, -0.10208, -0.00327,
        -0.53108,  1.10813, -0.07276,
        -0.07367, -0.00605,  1.07602
	);
	vec3 v = m1 * color;    
	vec3 a = v * (v + 0.0245786) - 0.000090537;
	vec3 b = v * (0.983729 * v + 0.4329510) + 0.238081;
	return pow(clamp(m2 * (a / b), 0.0, 1.0), vec3(1.0 / 2.2));	
}

float maxcomp(vec3 p) {return max(max(p.x,p.y),p.z);}

void join(inout float a, float b)
{
    a = min(a, b);
}

void subt(inout float a, float b)
{
    a = max(a, -b);
}

float box(vec2 p, vec2 s)
{
    return max((abs(p)-s).x,(abs(p)-s).y);
}

float digit(vec2 p, int d)
{
    float r = 100000000.0;
    switch(d)
    {
        case(0):
            join(r, box(p, vec2(0.1, 0.15)));
            subt(r, box(p, vec2(0.05, 0.1)));
            break;
        case(1):
            join(r, box(p, vec2(0.025, 0.15)));
            break;
        case(2):
            join(r, box(p, vec2(0.1, 0.15)));
            subt(r, box(p-vec2(0.15, -0.06), vec2(0.2, 0.035)));
            subt(r, box(p-vec2(-0.15, 0.06), vec2(0.2, 0.035)));
            break;
        case(3):
            join(r, box(p, vec2(0.1, 0.15)));
            subt(r, box(p-vec2(-0.15, -0.06), vec2(0.2, 0.035)));
            subt(r, box(p-vec2(-0.15, 0.06), vec2(0.2, 0.035)));
            break;
        case(4):
            join(r, box(p-vec2(0.075, 0.0), vec2(0.025, 0.15)));
            join(r, box(p-vec2(0.0, 0.05), vec2(0.1, 0.1)));
            subt(r, box(p-vec2(0.0, 0.09), vec2(0.05, 0.085)));
            break;
        case(5):
            join(r, box(p, vec2(0.1, 0.15)));
            subt(r, box(p-vec2(0.15, 0.06), vec2(0.2, 0.035)));
            subt(r, box(p-vec2(-0.15,-0.06), vec2(0.2, 0.035)));
            break;
        case(6):
            p = -p;
            join(r, box(p-vec2(0.075, 0.0), vec2(0.025, 0.15)));
            join(r, box(p-vec2(0.0, 0.05), vec2(0.1, 0.1)));
            subt(r, box(p-vec2(0.0, 0.05), vec2(0.05, 0.05)));
            break;
        case(7):
            join(r, box(p-vec2(0.0), vec2(0.1, 0.15)));
            subt(r, box(p-vec2(-0.05), vec2(0.1, 0.15)));
            break;
        case(8):
            join(r, box(p-vec2(0.0), vec2(0.1, 0.15)));
            subt(r, box(p-vec2(0.0, 0.05), vec2(0.05, 0.05)));
            subt(r, box(p-vec2(0.0, -0.05), vec2(0.05, 0.05)));
            join(r, box(p-vec2(0.0), vec2(0.1, 0.025)));
            break;
        case(9):
            join(r, box(p-vec2(0.075, 0.0), vec2(0.025, 0.15)));
            join(r, box(p-vec2(0.0, 0.05), vec2(0.1, 0.1)));
            subt(r, box(p-vec2(0.0, 0.05), vec2(0.05, 0.05)));
            break;
    }
    return r;
}

float getDig(float num, float d)
{
    float a = floor(num/pow(10., d));
    return a - floor(a/10.)*10.;
}

float print(vec2 p, float num, float spacing)
{
    float r = 10000.0;
    int fig = int(ceil(log2(num) / 3.32192809489)); // log base 10 of x equals log base 2 of x divided by log base 2 of 10
    for(int i = 0; i < fig; i++)
    {
        int di = int(getDig(num, float(i)));
        join(r, digit(p+vec2(i, 0)*spacing, di));
    }
    return r;
}

float perlinNoise(vec2 p, float s, float t)
{
    mat2 r = rotMat(t*2.0);
    vec2 tlVal = r*hash22(vec2(floor(p.x),  ceil(p.y)));
    vec2 blVal = r*hash22(vec2(floor(p.x), floor(p.y)));
    vec2 trVal = r*hash22(vec2( ceil(p.x),  ceil(p.y)));
    vec2 brVal = r*hash22(vec2( ceil(p.x), floor(p.y)));

    vec2 tlOffset = p - vec2(floor(p.x),  ceil(p.y));
    vec2 blOffset = p - vec2(floor(p.x), floor(p.y));
    vec2 trOffset = p - vec2( ceil(p.x),  ceil(p.y));
    vec2 brOffset = p - vec2( ceil(p.x), floor(p.y));

    float tl = dot(tlOffset, tlVal);
    float bl = dot(blOffset, blVal);
    float tr = dot(trOffset, trVal);
    float br = dot(brOffset, brVal);

    float a = mix(bl, tl, erp(fract(p.y), s));
    float b = mix(br, tr, erp(fract(p.y), s));

    return mix(a, b, erp(fract(p.x), s)) / 2.0 + 0.5;
}



float fbm(in vec2 x, float s, in float H, float time)
{    
    float G = exp2(-H);
    float f = 1.0;
    float a = 1.0;
    float t = 0.0;
    for( int i=0; i<8; i++ )
    {
        t += a*perlinNoise(f*x, s, time);
        f *= 2.0;
        a *= G;
    }
    return t * 0.5;
}

