#define KEYBOARD iChannel1
#define KEY_RESET 82

#define KEY_LEFT 37
#define KEY_UP 38
#define KEY_RIGHT 39
#define KEY_DOWN 40
#define KEY_A 65
#define KEY_W 87
#define KEY_D 68
#define KEY_S 83

// white red green blue black yellow purple half-white half-red
#define W vec4(1)
#define R  vec4(1,0,0,0)
#define G  vec4(0,1,0,0)
#define BL vec4(0,0,1,0) 
#define B vec4(0)
#define Y vec4(1,1,0,0)
#define P vec4(1,0,1,0)
#define HW vec4(0.5)
#define HR vec4(0.5,0,0,0)

#define hasNbr(c) (t==c||b==c||l==c||r==c)
#define hasNbr2(c) (t==c||b==c||l==c||r==c||tl==c||tr==c||bl==c||br==c)
#define allNbr(c) (t==c&&b==c&&l==c&&r==c)
#define allNbr2(c) (t==c&&b==c&&l==c&&r==c&&tl==c&&tr==c&&bl==c&&br==c)
//#define is(c) e==c

// Mechanics:
// W is player
// R gets pushed, travels in G, turns P to Y
// G gets pulled, turns into BL, turns into R
// BL is a wall, turns R into BL, turns into W
// B is empty space
// P turns into Y
// Y spawns R
// HW used to make player move once per button press
// HR used to make R travel in G

// Bugs:
// I think half reds can kill you, playing on paused is buggy

vec4 cell(in ivec2 p) {
    ivec2 r = ivec2(textureSize(iChannel0, 0));
    p = (p+r) % r;
    return texelFetch(iChannel0, p, 0 ).rgba;
}

bool key_down(int key) {
    return int(texelFetch(KEYBOARD, ivec2(key, 0), 0).x) == 1;
}

void mainImage( out vec4 col, in vec2 f )
{
    ivec2 px = ivec2(f);
    int frame = iFrame;

    col = vec4(0);
    if (frame == 0 || key_down(KEY_RESET)) {    
        col = vec4(0);
        if (length(f+.5-floor(0.5 * iResolution.xy)) < 1.)
            col = W;

        if (length(f+.5+vec2(3,-1)-floor(.5 * iResolution.xy)) < 1.)
            col = R; 

        if (length(f+.5-vec2(3,1)-floor(.5 * iResolution.xy)) < 1.)
            col = P;  

        return;
    }

    // Center cell
    vec4 e = cell(px);

    // Neighbour cells
    vec4 b = cell(px - ivec2(0,1));
    vec4 t = cell(px + ivec2(0,1));
    vec4 l = cell(px - ivec2(1,0));
    vec4 r = cell(px + ivec2(1,0));   
    vec4 bl = cell(px + ivec2(-1,-1));
    vec4 br = cell(px + ivec2(1, -1));
    vec4 tl = cell(px + ivec2(-1, 1));
    vec4 tr = cell(px + ivec2(1,  1));   

    col = e;

    int dirx = 0;
    int diry = 0;
    if (key_down(KEY_W) || key_down(KEY_UP))
        diry++;
    if (key_down(KEY_S) || key_down(KEY_DOWN))
        diry--;
    if (key_down(KEY_D) || key_down(KEY_RIGHT))
        dirx++;
    if (key_down(KEY_A) || key_down(KEY_LEFT))
        dirx--;

    // player is moving
    if (dirx != 0 || diry != 0) {
        vec4 oc = cell(px + ivec2(dirx, diry));
        vec4 c = cell(px - ivec2(dirx, diry));
        vec4 c2 = cell(px - ivec2(2 * dirx, 2 * diry));

        if (e == W) {
            if (c == G && oc == B) // fast travel in G
                col = G;
            else if (oc != BL && oc != Y && oc != P) // stop at walls
                col = B;          
        }
        else if (e == R) { // get pushed
            if (c == W)
                col = HW;
        } 
        else if (e == G) { // get fast travelled in
            if (c == W)
                col = W;       
        }
        else if (e == B) { 
            if (c == W) // get travelled in
                col = HW;
            else if (c == R && c2 == W) // get R travelled in
                col = R;
        }                 
    }    

    // player is not moving (reset to W after buttons released)
    else if (e == HW)
        col = W;

    // independent of player (after movement)
    if (e == R) {
        if (hasNbr(G)) 
            col = HR;
        else if (hasNbr(R))
            col = G;
        else if (hasNbr(BL))
            col = BL;
    }
    else if (e == G) {
        if (hasNbr(R))
            col = R;
        else if (allNbr2(G))
            col = BL;
    }
    else if (e == BL) {
        if (allNbr(BL))
            col = W;
        else if (allNbr(W))
            col = W;
    }
    else if (e == B) {
        if (hasNbr(Y))
            col = R;
        else if (allNbr(R))
            col = Y;
    }  
    else if (e == P) {
        if (hasNbr(R))
            col = Y;
    }
    else if (e == HR) {
        if (hasNbr(R))
            col = B; // change me to P for something weird
    } 


}

