// Displayed range of the graph.
const vec2 mini = vec2(-5, -2);
const vec2 maxi = vec2(5, 2);

#define StLine 0
#define StSolid 1
#define Style StLine

// Global vars used by overloads when you're lazy.
vec2 gFragCoord;
vec2 gResolution;

vec2 fragToView(vec2 fragCoord, vec2 resolution)
{
    return mix(mini, maxi, fragCoord / resolution);
}

vec2 viewToFrag(vec2 viewCoord, vec2 resolution)
{
    return (viewCoord - mini) / (maxi - mini) * resolution;
}

vec2 fragToView(vec2 fragCoord)
{
    return fragToView(fragCoord, gResolution);
}

vec2 viewToFrag(vec2 viewCoord)
{
    return viewToFrag(viewCoord, gResolution);
}

float fragToViewX(float fragCoordX, float resolutionX)
{
    return mix(mini.x, maxi.x, fragCoordX / resolutionX);
}

float viewToFragY(float viewCoordY, float resolutionY)
{
    return (viewCoordY - mini.y) / (maxi.y - mini.y) * resolutionY;
}

float fragToViewX(float fragCoordX)
{
    return fragToViewX(fragCoordX, gResolution.x);
}

float viewToFragY(float viewCoordY)
{
    return viewToFragY(viewCoordY, gResolution.y);
}

#define tf(func, fragCoord) (viewToFragY(func(fragToViewX(fragCoord.x))))

float getGraphOpacity( in float fragHeight, in float graphHeight )
{
    if(isnan(graphHeight) || isinf(graphHeight)) 
        return 0.0;

    float range = (1.0 + abs(dFdx(graphHeight)));

    #if Style == StLine
        range *= 2.0;
    #endif

    float upper = graphHeight + range;
    float lower = graphHeight - range;

    float value = (fragHeight - lower) / (upper - lower);

    #if Style == StLine
        value = (abs(value - 0.5) * 2.0);
    #endif

    return 1.0 - smoothstep(0., 1., value);
}

void drawGraph( inout vec4 fragColor, in float fragHeight, in float graphHeight, in vec4 color )
{
    float opacity = getGraphOpacity(fragHeight, graphHeight);

    vec4 c = color;
    c.a *= opacity;

    fragColor.rgb = mix(fragColor.rgb, c.rgb, c.a);
    fragColor.a = mix(fragColor.a, 1.0, c.a);
}

void drawGraph( inout vec4 fragColor, in float graphHeight, in vec4 color )
{
    drawGraph(fragColor, gFragCoord.y, graphHeight, color);
}

vec2 getGridOpacity(in vec2 fragCoord, in vec2 resolution, in vec2 gridSpacing)
{
    vec2 viewCoord = fragToView(fragCoord, resolution) / gridSpacing;
    vec2 nearestGridViewCoord = round(viewCoord) * gridSpacing;
    vec2 nearestGridFragCoord = viewToFrag(nearestGridViewCoord);

    const vec2 range = vec2(2);

    vec2 upper = nearestGridFragCoord + range;
    vec2 lower = nearestGridFragCoord - range;

    vec2 value = (fragCoord - lower) / (upper - lower);

    value = (abs(value - 0.5) * 2.0);

    return 1.0 - smoothstep(0., 1., value);
}

void drawGrid( inout vec4 fragColor, in vec2 fragCoord, in vec2 resolution, in vec2 gridSpacing, in vec4 colorHorizLines, in vec4 colorVertLines )
{
    vec2 opacity = getGridOpacity(fragCoord, resolution, gridSpacing);

    vec4 cH = colorHorizLines;
    cH.a *= opacity.y;
    vec4 cV = colorVertLines;
    cV.a *= opacity.x;

    float ratio = cV.a / (cV.a + cH.a);

    if(isnan(ratio) || isinf(ratio)) ratio = 0.5;

    vec4 c;
    c.rgb = mix(cH.rgb, cV.rgb, ratio);
    c.a = mix(cH.a, 1.0, cV.a);

    fragColor.rgb = mix(fragColor.rgb, c.rgb, c.a);
    fragColor.a = mix(fragColor.a, 1.0, c.a);
}

void drawGrid( inout vec4 fragColor, in vec2 gridSpacing, in vec4 colorHorizLines, in vec4 colorVertLines )
{
    drawGrid(fragColor, gFragCoord, gResolution, gridSpacing, colorHorizLines, colorVertLines);
}

