const float PI=3.1415926535;
const float SIDESPEED=.2;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord=(sin(iTime*.2)*10.6+22.6)*(fragCoord.xy-iResolution.xy/2.)/min(iResolution.y,iResolution.x);

    float sides=sin(iTime*SIDESPEED)*4.+6.;

    coord.x+=(sin(iTime+cos(coord.y))+sin(coord.x*2.))*(sin(iTime)+1.);
    coord.y+=(cos(iTime+sin(coord.x))+cos(coord.y*2.))*(sin(iTime)+1.);

    float angle=atan(coord.x,coord.y);
    float slice=PI*2./sides;
    float shape=cos(floor(.5+angle/slice)*slice-angle)*length(coord);

    vec3 color=vec3(sin(shape*(cos(iTime*.1+26.)*2.+3.)),sin(shape*(cos(iTime*.1+25.)*2.+3.)),sin(shape*(atan(iTime*.1+13.)*2.+3.)));
    fragColor=vec4(color,1.);
}

float shape(vec2 fragmentPosition,float radius,vec2 realPosition){

    return step(radius/fragmentPosition.x/fragmentPosition.y,length(fragmentPosition-realPosition));

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 fragmentPosition=fragCoord/min(iResolution.y,iResolution.x);

    float circleRadius=.04;

    float frequency=1.;
    float range=sin(iTime)*4.+6.;
    vec2 center=vec2(.7,.5);

    //vec2 circlePosition=iMouse.xy/min(iResolution.y,iResolution.x);
    vec2 circlePosition=vec2((sin(iTime*frequency)/range)+center.x,(cos(iTime*frequency)/range)+center.y);

    vec3 color=vec3(shape(fragmentPosition,circleRadius,circlePosition));

    if(length(color)==0.){
        color=vec3(vec2(fragCoord/iResolution.xy),sin(iTime)*.3+.7);

    }
    else{
        color=vec3(sin(iTime)*.3+.7,vec2(fragCoord/iResolution.xy));
    }

    fragColor = vec4(color,1.0);
}

void mainImage( out vec4 c, in vec2 f)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 r = iResolution.xy, u = (2. * f-r)/r.y;

    float a = iTime * 0.5, ca = cos(a), sa = sin(a);
    u *= mat2(ca, -sa, sa, ca);
    float rad = length(u);
    float ang = (atan(u.y, u.x) + radians(180.)) /radians(360.);


    vec2 polar = vec2(rad, ang) +iTime * 0.01;
