#define PI 3.14159
#define TWO_PI 6.28318530718
// Morse code audio frequency
#define FREQ 800.0

const float DL = 0.05; // Dot (dit) length
const float SL = DL; // Space length
const float LS = DL * 3.0; // Letter space length
const float WS = DL * 7.0; // Word space length
const float _L = DL * 3.0; // Dash (dah) length

float ct = 0.0; // Current time
float st = 0.0; // Start time used for note timings
struct Wave {
    float frequency;
    float amplitude;
    float phase;
};

// Pause for duration 'd'
float p(float d) {
    st += d;
    return 0.0;
}

// Play frequency 'freq' for duration 'd'
float f(float freq, float d) {
    float fq = (ct >= st && ct < (st + d)) ? freq : 0.0;
    p(d);
    return fq;
}

// Makes a wave from frequency, amplitude and time
float w(Wave w) {
    return w.amplitude * sin(w.phase * w.frequency * TWO_PI);
}

float dit() {
    return f(FREQ, DL) + p(SL);
}

float dah() {
    return f(FREQ, _L) + p(SL);
}

float ls() {
    return p(LS - SL);
}

float ws() {
    return p(WS - SL);
}

float A() {
    return dit() + dah() + ls();
}

float B() {
    return dah() + dit() + dit() + dit() + ls();
}

float C() {
    return dah() + dit() + dah() + dit() + ls();
}

float D() {
    return dah() + dit() + dit() + ls();
}

float E() {
    return dit();
}

float F() {
    return dit() + dit() + dah() + dit() + ls();
}

float G() {
    return dah() + dah() + dit() + ls();
}

float H() {
    return dit() + dit() + dit() + dit() + ls();
}

float I() {
    return dit() + dit() + ls();
}

float J() {
    return dit() + dah() + dah() + dah() + ls();
}

float K() {
    return dah() + dit() + dah() + ls();
}

float L() {
    return dit() + dah() + dit() + dit() + ls();
}

float M() {
    return dah() + dah() + ls();
}

float N() {
    return dah() + dit() + ls();
}

float O() {
    return dah() + dah() + dah() + ls();
}

float P() {
    return dit() + dah() + dah() + dit() + ls();
}

float Q() {
    return dah() + dah() + dit() + dah() + ls();
}

float R() {
    return dit() + dah() + dit() + ls();
}

float S() {
