}

float Z() {
    return dah() + dah() + dit() + dit() + ls();
}

float w(float freq) {
    return freq + ws();
}

vec2 mainSound(int samp, float time) {
    ct = time;
    float amplitude = 0.1;
    float frequency = ws() + w(W() + H() + A() + T()) + w(A() + R() + E()) + w(Y() + O() + U()) + w(D() + O() + I() + N() + G());

    Wave w1 = Wave(frequency, amplitude, time);

    vec2 r = vec2(w(w1));

    return r * 0.2;
}

