#define rn 64.

float getFreq(float x) {
	return texture(iChannel0, vec2(floor(x * rn + 1.) / rn, 0)).x;
}

float getFreq_smooth(float x) {
	float index = floor(x * rn) / rn;
    float next = floor(x * rn + 1.0) / rn;
	return mix(getFreq(index), getFreq(next), smoothstep(0.0, 1.0, fract(x * rn)));
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float sdBox(in vec2 p, in vec2 b){
    vec2 d = abs(p)-b;
    return length(max(d,0.))+min(max(d.x,d.y),0.);
}

float sdSeg(vec3 p, vec3 a, vec3 b, float r) {
  vec3 pa = p - a, ba = b - a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
  return length( pa - ba*h ) - r;
}

vec3 rayOri() {
    vec2 m = iMouse.xy/iResolution.xy;
    float th = tanh(0.08 * iTime);
    vec3 ro = vec3(0, 13, 3. + 40. * (1.-th));
    if (iTime > 14.) {
        float a = 2. * pi * h21(vec2(floor(0.5 * iTime), 11.));
        if (iTime < 40.)
            ro.xz *= rot(a + iTime * 10.);
        else if (iTime > 90. && iTime < 105.) {
            ro = vec3(0, 13, 3.);
            ro.xz *= rot(iTime * 4.);
        }            
        else
            ro.xz *= rot(a + iTime);
        ro.y = 2. * a / pi;
    }
    if (iTime > 126.)
        ro.x += 0.2 * iTime - 126.;
    //ro.yz *= rot(-m.y*3.14+1.);
    //ro.xz *= rot(-m.x*6.2831);
    return ro;
}

float dist(vec3 p) {
    float pd = p.y;
    float f = getFreq_smooth(0.);
    float s = 0.04;

    if (iTime > 90. && iTime < 127.) {
        pd += 5. * f;
    }
    else {
        if (iTime > 40.) {
            //p.x += iTime * 1000.;
            p.xz = mod(p.xz, s * iTime) - 0.5 * s * iTime;
            p *= 0.6;
            if (iTime > 50.)
                p.xz *= rot(4. * iTime);
                if (iTime > 60.) {
                p.y -= 1.5;
                p.yz *= rot(10. * iTime);
                p.y += 1.5;
            }
    }     
    }

    float sc = 2. * pow(f, 2.);//1.5 + 0.1 * thc(4., 2. * pi * h21(p.xz) + 10. * iTime);

    if (iTime > 14.)
        p *= sc;
    else
        p *= 0.2;
    if (iTime < 40.) {
        p.y -= 1.5;

        p.xy *= rot(pi-pi * thc(4., pi/2. + iTime));
        p.y += 1.5;
        p.xz *= rot(10. * thc(4., iTime));
    }
    float sd = length(p - vec3(0,3,0)) - 0.3;
    float ld = sdSeg(p, vec3(0,1.5,0), vec3(0,3,0), 0.1);
    float ld2 = sdSeg(p, vec3(-1,2.2,0), vec3(1,2.2,0), 0.075);
    float ld3 = sdSeg(p, vec3(0,1.5,0), vec3(0.5,0,0), 0.1);
    float ld4 = sdSeg(p, vec3(0,1.5,0), vec3(-0.5,0,0), 0.1);    
    float d = min(pd, sd);
    d = smin(d, ld, 0.12);
    d = smin(d, ld2, 0.04);
    d = min(d, ld3);
    d = min(d, ld4);

    if (iTime < 14.)
        return d / (2.);
    return d / sc;
}

float march(vec3 ro, vec3 rd, float z) {

    float dO=0.;
    float s = sign(z);
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = dist(p);
        if (s != sign(dS)) { z *= 0.5; s = sign(dS); }
        if(abs(dS)<SURF_DIST || dO>MAX_DIST) break;
        dO += dS*z; 
    }

    return min(dO, MAX_DIST);
}

vec3 normal(vec3 p) {
	float d = dist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        dist(p-e.xyy),
        dist(p-e.yxy),
        dist(p-e.yyx));

    return normalize(n);
}

vec3 rayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

