    float h = h21(vec2(131., floor(0.5 * iTime)));
    float th = tanh(0.2 * max(0., iTime - 12.));
    if (iTime < 14.) {
        th = 16.;
        h = 0.;
        fr = 1.;
    }
    vec3 rd = rayDir(uv, ro, vec3(0, th + h,0), 0.+ fr);
    if (iTime > 90. && iTime < 105.)
        rd = rayDir(uv, ro, vec3(0, th + h,0), 1.);

    vec3 col = vec3(0);

    float d = march(ro, rd, 1.);

    vec3 p = ro + rd * d;
      vec3 n = normal(p);
    float IOR = 1.2;
    if(d<MAX_DIST) {        

        vec3 r = reflect(rd, n);


        vec3 lightDir = normalize(vec3(cos(iTime),0.5 * sqrt(2.),sin(iTime)));
        float dif  = dot(n,  lightDir)*.5+.5;

        float f = getFreq_smooth(0.);

        col = vec3(dif) * 10. * th * pow(f, 10.) * pal(4. * f, vec3(0,1,2)/3.); 
        if (p.y < 0.01) {
            col *= 1./exp(f * 0.25 * length(p.xz));
            col *= th;
            col *= 0.75 + 0.25 * thc(4., 2. * pi * f + log(length(p.xz)) * 6. - iTime * 10.);

            if (iTime > 40. && iTime < 105.)
                col *=.2 + .8 *mod(floor(0.39 * p.x) + floor(0.4 * p.z), 2.);
        }
        else 
            col = mix(vec3(1,0,0), vec3(1), 1.-sqrt(f)) * dif;
    } 

   // if (iTime > 0.)
    vec3 tx = texture(iChannel1, p).rgb;
    col = mix(col, tx, 1.-tanh(0.25 * iTime));
    if (iTime > 105.)
        col = mix(col, tx, 0.5 + .5*thc(4., iTime));      

    col = pow(col, vec3(.4545));	// gamma correction

    fragColor = vec4(col,1.0);
}

