    float fade2 = 1.0;
    if (fill>thresh)
        fade *= 1.0-clamp((fill-thresh)*0.05,0.0,1.0);

    // render circle
    float rad = 0.0+(fade*0.8);
    float height = mix(0.00,0.08,fade);    
    float inner = rad-height;
    float outer = rad+height;
    float rt = mix(0.0,0.65,fade);
    float d = 1.0;
    if (height>0.0)
        d = sdDashedCircle(p,at,inner,outer,totalsegs,rt);

    float aa =  1.5*rt*fwidth(at);// 0.005
    float cc = smoothstep(aa,0.0,d);
    return vec4(mix(col2*cc,col1*cc,fade),cc);
}


