////////////////////////////////////////////////////////////////////////////////
//
// SVG Interpreter, mla, 2022
//
// Drawing fairly lightly processed SVG data, using 16-bit fixed 
// point data packed into a single array. Each path includes a 
// bounding box to help speed things up.
//
// Only L, C and M command are handled - relative commands are made
// absolute and H,V and z commands replaced by L. Dimensions are
// centered and rescaled to [0,1].
//
// Compile time on Windows/Nvidia GTX 1650 is a reasonable 1.4 seconds,
// much less on Linux/Intel/Mesa and Linux/Nvidia. 60fps at HD
// resolution for Nvidia, 40 fps at 512x288 for Intel.
//
// Data array generated by a little C++ program you can see in Buffer B
// (this is for information only and is entirely unsupported).
//
// <mouse>: pan and zoom
// 'r': autorotation
// 'x': recalculate buffer data each frame (only useful with USETEXTURE)
//
// See also:
//
// https://www.shadertoy.com/view/DdsXRl by @alro
// https://www.shadertoy.com/view/Wtt3Wl by @NinjaKoala
//
////////////////////////////////////////////////////////////////////////////////

// Uncomment this to read data from Buffer A rather than directly from memory.
#define USETEXTURE

uvec4 fetchdata(int i) {
#if !defined USETEXTURE
  return data[i];
#else
  return floatBitsToUint(texelFetch(iChannel0,ivec2(i%64,i/64),0));
#endif
}

// 16-bit data packed in pairs into a uvec4
uint getdata0(int i) {
  uvec4 v = fetchdata(i>>3);
  i &= 7;
  uint t = v[i>>1];
  if ((i&1) == 0) t >>= 16;
  return t & 0xffffu;
}

uint getdata(int i) {
  // This version might be faster on some platforms
  uvec4 v = fetchdata(i/8);
  i %= 8;
  uint t = v[i/2];
  if (i%2 == 0) t >>= 16;
  return t & 0xffffu;
}

vec2 getpos(int i) {
  float x = float(getdata(i))/65535.0;
  float y = float(getdata(i+1))/65535.0;
  return vec2(x,y);
}

void polywind(vec2 p, vec2 q, vec2 r);
float segment(vec2 p, vec2 a, vec2 b);
float bezier(vec2 p, mat4x2 m);

float draw(vec2 pos) {
  float eps = fwidth(length(pos)); // Bounding box border
  int i = 0;
  float d = 1e8;
  vec2 p;
  while (true) {
    int instr = int(getdata(i++)); // Increment i for command
    // Instruction format is:
    // <drawtype:3> <nargs:2> <count:11>
    if (instr == 0) break;
    int drawtype = instr>>13;
    int nargs = (instr>>11)&3;
    int count = instr&((1<<11)-1);
    vec2 p0, p1, p2;
    if (nargs > 0) p0 = getpos(i);
    if (nargs > 1) p1 = getpos(i+2);
    if (nargs > 2) p2 = getpos(i+4);
    i += 2*nargs; // Increment i for arguments
    if (drawtype == 1) {
      // Cubic Bezier, drawn as line segments
      vec2 minbox = min(min(p,p0),min(p1,p2))-eps;
      vec2 maxbox = max(max(p,p0),max(p1,p2))+eps;
      if (any(lessThan(pos,minbox)) || any(greaterThan(pos,maxbox))) {
        polywind(pos,p,p2);
      } else if (dot(p-p2,p-p2) < 10.0*eps*eps) {
        d = min(d,segment(pos,p,p2));
      } else {
        d = min(d,bezier(pos,mat4x2(p,p0,p1,p2)));
      }
      p = p2;
    } else if (drawtype == 2) {
      // A plain old line segment
      vec2 minbox = min(p,p0)-eps;
      vec2 maxbox = max(p,p0)+eps;
      if (any(lessThan(pos,minbox)) || any(greaterThan(pos,maxbox))) {
        polywind(pos,p,p0);
      } else {
        d = min(d,segment(pos,p,p0));
      }
      p = p0;
    } else if (drawtype == 3) {
      // Move
      p = p0;
    } else if (drawtype == 4) {
      // Start of path, skip if out of bounding box
      p = vec2(0);
      vec2 minbox = p0-eps, maxbox = p1+eps;
      // If outside path bounding box, skip entire path.
      if (any(lessThan(pos,minbox)) || any(greaterThan(pos,maxbox))) i += count;
    }
  }
  return d;
}


int wind = 0; // Compute winding number here
void polywind(vec2 p, vec2 q, vec2 r) {
  // NR winding number computation
  q -= p; r -= p; // Relative to p
  float t = determinant(mat2(q,r));
  if (q.y <= 0.0) {
    if (r.y > 0.0 && t > 0.0) wind++;
  } else {
    if (r.y <= 0.0 && t < 0.0) wind--;
  }
}

float segment(vec2 p, vec2 a, vec2 b) {
  polywind(p,a,b);
  p -= a; b -= a;
  float h = dot(p,b)/dot(b,b);
  h = clamp(h, 0.0, 1.0);
  return length(p-b*h);
}

const int nbeziercoeffs = 16;
vec4 beziercoeff(int i) {
  // Matrix formulation due to Shane.
  const int N = nbeziercoeffs;
  float t = float(i)/float(N);
  float u = 1.0-t, t2 = t*t, u2 = u*u;
  return vec4(u*u2,3.0*t*u2,3.0*u*t2,t*t2);
}

// Distance to Cubic Bezier.
// Split the path into N subsegments.
float bezier(vec2 p, mat4x2 m) {
  // Since we spend most of the time deciding not to draw
  // curves, the value of N doesn't seem to be time critical.
  const int N = nbeziercoeffs;
  vec2 q = m[0];
  float d = 1e8;
  for (int i = 1; i <= N; i++) {
    vec2 r = m*beziercoeff(i);
    d = min(d,segment(p,q,r));
    q = r;
  }
  return d;
}

// Rotate vector p by angle t.
vec2 rotate(vec2 p, float t) {
  return cos(t)*p + sin(t)*vec2(-p.y,p.x);
}

void mainImage( out vec4 fragcolor, in vec2 fragcoord ) {
  vec3 col = vec3(0.5);
  float theta = 0.0;
  if (!key(CHAR_R)) theta = 0.5*iTime;
  float scale = 1.0/cos(theta);
  // SVG is resized to have largest dimension 1 and centred about
  // 0.5 in the other dimension. Here x is largest, so scale by that.
  vec2 uv = (fragcoord-0.5*iResolution.xy)/iResolution.x;
  if (iMouse.z > 0.0) {
    uv -= 4.0*(iMouse.xy - 0.5*iResolution.xy)/iResolution.x;
    scale /= 4.0;
  }
  float px = abs(scale/iResolution.x);
  uv *= scale;
  uv.y = -uv.y; // Keep SVG y direction
  uv = rotate(uv,theta);
  if (all(lessThanEqual(abs(uv),0.5*datadims+2.0*px))) { 
    float d = draw(uv+0.5); //// SVG is in [0,1] box
    if ((wind&1) != 0) col = vec3(1,0,0);
    col *= smoothstep(0.0,px,d);
  };
  if (alert) col.b = 1.0;
  fragcolor = vec4(col,1);
}

