// ---------------------------------------------------------------------------------------
//	Created by fenix in 2022
//	License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
//
//  Beneath a soft blanket of mist, arctic fireflies make highways of the valleys
//  between the glaciers.
//
//  The techniques here are an evolution of those in Spark on Swiss Cheese Mountain:
//
//      https://www.shadertoy.com/view/7tyyW1
//
//  Although the terrain FBM is now based on jarble's Glacial valleys:
//
//      https://www.shadertoy.com/view/NscGWl
//
//  The main idea is to use deferred rendering to allow each particle to be a local
//  light source. Potentially thousands of light sources are possible, but only four
//  can affect each pixel so they need to be spaced out to avoid artifacts.
//
//  I also attempted to create a volumetric fog near the ground, which I think mostly
//  worked as intended. The effect is a combination of gaussian blur and height-based
//  lightening. It does obscure the light effect somewhat, though, so to see the
//  particles and lighting more clearly, hold down the ALT key. It also hides some
//  artifacts so it seemed like a net win for the shader.
//
//  I struggled with anti-aliasing as usual, but I eliminated a lot of the artifacts
//  while keeping most of the speed, so I'm mostly happy with the result. You can disable
//  MSAA with SHIFT and FXAA with CTRL. There are a few ideas that might be interesting:
//
//  * When doing multisample raymarches, don't start from the camera position for each
//    sample. The t value of the first sample provides a good starting place for the
//    other samples.
//
//  * Multisampling is done during the G buffer pass, so per-sample calculations are
//    the absolute minimum. Combined with the warm start march, I was able to leave MSAA
//    set to 9x. 16x, 25x are probably worth trying with a faster GPU.
//
//  * FXAA is applied during the final render to areas not being blurred. It's not
//    as significant a difference as the MSAA but it does help with some high frequency
//    shadow areas.
//
//  * A slight temporal blur is applied at low resolutions only, beacuse at low frame
//    rates it looks bad. I tuned this based on my graphics card, so fingers crossed
//    the settings work well enough for everyone. I tried basing it on iFrameRate and
//    iTimeDelta but having it switch back and forth looks terrible. I don't like it
//    but it is the only way I could find to tame some aliasing vibrations along
//    thin shadow boundaries.
//
//  This is my first attempt at stars or a moon, so I have no idea if there are better
//  ways. Of course I am happy to discuss ideas about any part of this (or any other)
//  shader!
//
//  Buffer A computes the particle positions
//  Buffer B computes nearest particles to each screen pixel
//  Buffer C renders the background with depth in the w component
//  Buffer D combines scene
//  Image applies fog and distance blur (and re-renders for adaptive AA)
//
// ---------------------------------------------------------------------------------------

#if FXAA
// From reinder's  Post process - FXAA
//    https://www.shadertoy.com/view/ls3GWS
// he got it from:
//    http://www.geeks3d.com/20110405/fxaa-fast-approximate-anti-aliasing-demo-glsl-opengl-test-radeon-geforce/3/
#define FXAA_SPAN_MAX 8.0
#define FXAA_REDUCE_MUL   (1.0/FXAA_SPAN_MAX)
#define FXAA_REDUCE_MIN   (1.0/128.0)
#define FXAA_SUBPIX_SHIFT (1.0/4.0)

vec3 AArender( vec2 uv2 )
{    
    uv2 /= iResolution.xy;
    vec2 rcpFrame = 1. / iResolution.xy;
    vec4 uv = vec4( uv2, uv2 - (rcpFrame * (0.5 + FXAA_SUBPIX_SHIFT)));

    vec3 rgbNW = textureLod(iChannel2, uv.zw, 0.0).xyz;
    vec3 rgbNE = textureLod(iChannel2, uv.zw + vec2(1,0)*rcpFrame.xy, 0.0).xyz;
    vec3 rgbSW = textureLod(iChannel2, uv.zw + vec2(0,1)*rcpFrame.xy, 0.0).xyz;
    vec3 rgbSE = textureLod(iChannel2, uv.zw + vec2(1,1)*rcpFrame.xy, 0.0).xyz;
    vec3 rgbM  = textureLod(iChannel2, uv.xy, 0.0).xyz;

    vec3 luma = vec3(0.299, 0.587, 0.114);
    float lumaNW = dot(rgbNW, luma);
    float lumaNE = dot(rgbNE, luma);
    float lumaSW = dot(rgbSW, luma);
    float lumaSE = dot(rgbSE, luma);
    float lumaM  = dot(rgbM,  luma);

    float lumaMin = min(lumaM, min(min(lumaNW, lumaNE), min(lumaSW, lumaSE)));
    float lumaMax = max(lumaM, max(max(lumaNW, lumaNE), max(lumaSW, lumaSE)));

    vec2 dir;
    dir.x = -((lumaNW + lumaNE) - (lumaSW + lumaSE));
    dir.y =  ((lumaNW + lumaSW) - (lumaNE + lumaSE));

    float dirReduce = max(
        (lumaNW + lumaNE + lumaSW + lumaSE) * (0.25 * FXAA_REDUCE_MUL),
        FXAA_REDUCE_MIN);
    float rcpDirMin = 1.0/(min(abs(dir.x), abs(dir.y)) + dirReduce);

    dir = min(vec2( FXAA_SPAN_MAX,  FXAA_SPAN_MAX),
          max(vec2(-FXAA_SPAN_MAX, -FXAA_SPAN_MAX),
          dir * rcpDirMin)) * rcpFrame.xy;

    vec3 rgbA = (1.0/2.0) * (
        textureLod(iChannel2, uv.xy + dir * (1.0/3.0 - 0.5), 0.0).xyz +
        textureLod(iChannel2, uv.xy + dir * (2.0/3.0 - 0.5), 0.0).xyz);
    vec3 rgbB = rgbA * (1.0/2.0) + (1.0/4.0) * (
        textureLod(iChannel2, uv.xy + dir * (0.0/3.0 - 0.5), 0.0).xyz +
        textureLod(iChannel2, uv.xy + dir * (3.0/3.0 - 0.5), 0.0).xyz);

    float lumaB = dot(rgbB, luma);

    if((lumaB < lumaMin) || (lumaB > lumaMax)) return rgbA;

    return rgbB; 
}
#endif // FXAA

// based on gaussian blur from FabriceNeyret2's smart gaussian blur: https://www.shadertoy.com/view/WtKfD3
// this is used for fog and distance blurring

int           N =  11;                              // target sampling rate
float         w,                                   // filter width
              z;                                        // LOD MIPmap level to use for integration 
#define init  z = ceil(max(0.,log2(w*iResolution.y/float(N))));   // N/w = res/2^z

vec4 convol2D(vec2 U) {                                                     
    vec4  O = vec4(0.0);  
    float r = float(N-1)/2., g, t=0.;                                       
    for( int k=0; k<N*N; k++ ) {                                            
        vec2 P = vec2(k%N,k/N) / r - 1.;                                    
        t += g = exp(-2.*dot(P,P) );                                        
        O += g * textureLod(iChannel2, (U+w*P) *iResolution.y/iResolution.xy, z );  
    }                                                                       
    return O/t;                                                             
}      

void mainImage( out vec4 O, vec2 u )
{
    float depth = texelFetch(iChannel2, ivec2(u), 0).w;
    vec4 state = texelFetch(iChannel1, ivec2(0), 0);
    g_GroundFog = state.z == 0.;

    vec3 cameraLookAt, cameraPos, cameraFwd, cameraLeft, cameraUp;
    fxCalcCamera(state, iTime, cameraLookAt, cameraPos, cameraFwd, cameraLeft, cameraUp);

    mat4 c2w = fxCalcCameraMat(iResolution, cameraLeft, cameraUp, cameraFwd, cameraPos);
    mat4 w2c = inverse(c2w);

	vec3 rayDir = fxCalcRay(u, iResolution, cameraFwd, cameraUp, cameraLeft);

    vec3 groundPos = cameraPos + rayDir * depth;

    // Blur based on depth and height from ground
    w = max(0., max((depth-15.)*.0, g_GroundFog ? .5-groundPos.y : 0.)) * 0.03;
    if (depth > FAR_CLIP) w = 0.; // except don't blur sky

    if (w > 0.002)
    {
        init
        vec2 p = (u - iResolution.xy * 0.5) / iResolution.y;
        {
            vec2 U = u / iResolution.y;  
            O = convol2D(U);
        }
    }
    else
    {
#if FXAA
        if (!keyDown(KEY_CTRL) && depth < FAR_CLIP)
        {
            O.xyz = AArender(u);
        }
        else
#endif
        O = texture(iChannel2, u/iResolution.xy);
    }

    O.xyz = ACESFilm(sqrt(O.xyz));

    O.w = 1.;
}

common_code:
const float MAX_AGE = 30.0;
const int MAX_PARTICLES = 10000;
const float PARTICLE_SPEED = 0.01;
#define PER_PARTICLE_LIGHTING 1
#define SHADOWS 1 // Disable for faster speed
#define FXAA 1
#define TEMPORAL_BLUR 1
#define FOG_BLUR 1
const vec3 GROUND_FOG_COLOR = vec3(.03, .03, .05);
const int TERRAIN_ITERS = 7;
const int SHADOW_ITERS = 5;
const float FAR_CLIP = 20.;
const vec3 MOON_DIR = normalize(vec3(0, .2, 1));
const vec3 MOON_COLOR = vec3(.1, .15, .3);

bool g_GroundFog = true;

uvec4 hash(uvec4 x){
    x = ((x >> 16u) ^ x.yzwx) * 0x45d9f3bu;
    x = ((x >> 16u) ^ x.yzwx) * 0x45d9f3bu;
    x = ((x >> 16u) ^ x.yzwx) * 0x45d9f3bu;
    x = ((x >> 16u) ^ x.yzwx) * 0x45d9f3bu;
    //x = (x >> 16u) ^ x;
    return x;
}

uvec4 hash(uvec3 x0){
    uvec4 x = x0.xyzz;
    x = ((x >> 16u) ^ x.yzxy) * 0x45d9f3bu;
    x = ((x >> 16u) ^ x.yzxz) * 0x45d9f3bu;
    x = ((x >> 16u) ^ x.yzxx) * 0x45d9f3bu;
    //x = (x >> 16u) ^ x;
    return x;
}

vec4 noise(ivec4 p){
    const float scale = pow(2., -32.);
    uvec4 h = hash(uvec4(p));
    return vec4(h)*scale;
}

vec4 noise(ivec3 p){
    const float scale = 1.0/float(0xffffffffU);
    uvec4 h = hash(uvec3(p));
    return vec4(h)*scale;
}

vec4 noise(ivec2 p){
    return noise(ivec3(p, 0));
}

//hashing noise by IQ
float hash( int k ) {
    uint n = uint(k);
	n = (n << 13U) ^ n;
    n = n * (n * n * 15731U + 789221U) + 1376312589U;
    return uintBitsToFloat( (n>>9U) | 0x3f800000U ) - 1.0;
}

// From jarble's Glacial valleys: https://www.shadertoy.com/view/NscGWl
float fbm(in vec2 uv,int octaves)
{
    //this function generates the terrain height
    uv *= 1.25;
    float value = 0.;
    float amplitude = 1.2;
    float freq = 0.5,n2=0.;
    vec2 n1 = vec2(0.);
    for (int i = 0; i < octaves; i++)
    {
        n2 = sin(uv.x*freq)+cos(uv.y*freq);

        // From Dave_Hoskins https://www.shadertoy.com/user/Dave_Hoskins
        value = abs(value-abs(n2-value) * amplitude);
        amplitude *= .37;
        freq *= 2.05;
        uv = vec2(uv.y,uv.x+n2/(freq));
    }

    return value;
}

float f(in vec3 p,int iters)
{   
    float h = fbm(p.xz,iters);
    return h;
}

vec3 getNormal(vec3 p, float t, int iters)
{
    vec3 eps=vec3(.001 * t, .0, .0);
    vec3 n=vec3(f(p - eps.xyy, iters) - f(p + eps.xyy, iters),
                1. * eps.x,
                f(p - eps.yyx, iters) - f(p + eps.yyx, iters));

    return normalize(n);
}

float rayMarching(in vec3 ro, in vec3 rd, float tMin, float tMax, int octaves)
{
    float t = tMin;
	for( int i = 0; i < 200; i++ )
	{
        vec3 pos = ro + t * rd;
		float h = pos.y - f(pos,octaves);
		if( abs(h) < (0.001 * t) || t > tMax)
            break;
		t += 0.5 * h;
	}

	return t;
}

vec3 camPath(float time)
{
    vec3 camPos = vec3(cos(time*.2)*.4 + time*.2, cos(time*.2)*.5 + 1.5, 2.0 + (time*.2));
    camPos.y = max(camPos.y, f(camPos, 3) + .1);
    return camPos;
}

float cross2(vec2 a, vec2 b)
{
    return a.x * b.y - a.y * b.x;
}

mat2 rotMat(float a)
{
    vec2 sc = vec2(cos(a), sin(a));
    return mat2(sc.x, sc.y, -sc.y, sc.x);
}

void fxCalcCamera(in vec4 state, in float iTime, out vec3 cameraLookAt, out vec3 cameraPos, out vec3 cameraFwd, out vec3 cameraLeft, out vec3 cameraUp)
{
    cameraLookAt = camPath(iTime);
    cameraPos	= camPath(iTime - 2.);
    cameraLookAt.y = max(cameraLookAt.y, cameraPos.y - .1 - state.y);
    cameraPos.y = cameraLookAt.y + .1 + state.y;
    vec3 cameraNextPos	= camPath(iTime + 2.);

    cameraFwd  = normalize(cameraLookAt - cameraPos);
    cameraFwd.xz *= rotMat(state.x*4.);
    float tilt = -.5 * cross2(cameraNextPos.xz - cameraPos.xz, cameraPos.xz - cameraLookAt.xz);
    cameraLeft = -normalize(cross(cameraFwd, vec3(0.0,1.0,0.0)) + vec3(0, tilt, 0));
    cameraUp   = normalize(cross(cameraLeft, cameraFwd));
}

mat4 fxCalcCameraMat(vec3 resolution, vec3 cameraLeft, vec3 cameraUp, vec3 cameraFwd, vec3 cameraPos)
{
    return mat4(vec4(-0.5 * cameraLeft, 0.0),
        vec4(-0.5*cameraUp, 0.0),
        vec4(cameraFwd, 0.0),
        vec4(cameraPos, 1.0));
}

vec3 fxCalcRay(in vec2 fragCoord, in vec3 iResolution, in vec3 cameraFwd, in vec3 cameraUp, in vec3 cameraLeft)
{
	vec2 screenPos = (fragCoord - .5 * iResolution.xy) / iResolution.y;
	return normalize(cameraFwd - screenPos.x * cameraLeft - screenPos.y * cameraUp);
}

float length2(vec2 v)
{
    return dot(v, v);
}

float fxLinePointDist2(vec2 a, vec2 b, vec2 p, float ar)
{
    p -= a, b -= a;
    float h = clamp(dot(p, b) / dot(b, b), 0., 1.);// proj coord on line
    return length2((p - b * h) * vec2(ar, 1)); // squared dist to segment
}

const float PI = 3.141592653589793;

//returns the ids of the four closest particles from the input
ivec4 fxGetClosestInternal(sampler2D sampler, ivec2 xy)
{
    return ivec4(texelFetch(sampler, xy, 0));
}

#define fxGetClosest(X) fxGetClosestInternal(iChannel1, X)

#define POS 0
#define VEL 1
#define NUM_PARTICLE_DATA_TYPES 2

//returns the location of the particle within the particle buffer corresponding with the input id 
ivec2 fxLocFromIDInternal(int width, int id, int dataType)
{
    int index = id * NUM_PARTICLE_DATA_TYPES + dataType;
    return ivec2( index % width, index / width);
}

#define fxLocFromID(X, Y) fxLocFromIDInternal(int(iResolution.x), X, Y)

struct fxParticle
{
    vec3 pos;
    vec3 vel;
    float age;
};

//get the particle corresponding to the input id
fxParticle fxGetParticleInternal(sampler2D sampler, int resolutionWidth, int id)
{
    vec4 particleData0 = texelFetch(sampler, fxLocFromIDInternal(resolutionWidth, id, POS), 0);
    vec4 particleData1 = texelFetch(sampler, fxLocFromIDInternal(resolutionWidth, id, VEL), 0);

    fxParticle particle;
    particle.pos = particleData0.xyz;
    particle.vel = particleData1.xyz;
    particle.age = particleData0.w;

    return particle;
}

vec4 fxSaveParticle(fxParticle p, int dataType)
{    
    switch(dataType)
    {
    case POS:  
        return vec4(p.pos, p.age);
    case VEL:  
        return vec4(p.vel, 0);
    }
}

#define fxGetParticle(X) fxGetParticleInternal(iChannel0, int(iResolution.x), X)

vec4 fxGetParticleDataInternal(sampler2D sampler, int resolutionWidth, int id, int dataType)
{
    return texelFetch(sampler, fxLocFromIDInternal(resolutionWidth, id, dataType), 0);
}

#define fxGetParticleData(X, Y) fxGetParticleDataInternal(iChannel0, int(iResolution.x), X, Y)

#define keyDown(ascii)    ( texelFetch(iChannel3,ivec2(ascii,0),0).x > 0.)

#define KEY_SHIFT 16
#define KEY_CTRL 17
#define KEY_ALT 18
#define KEY_SPACE 32

vec3 sky(vec3 rayDir, vec3 res)
{
    vec3 stars = vec3(smoothstep(.5, .8, pow(float(hash(uvec4(rayDir * res.y + 4000., 1)).r) / 4.35e9, 50.))) * 4.;
    vec3 moon = MOON_COLOR * (1. + f(rayDir.xxy * 100., 3));
    return mix(moon, stars, smoothstep(.03, .03 + 2./res.y, distance(rayDir, MOON_DIR)));
}

vec3 lighting(vec3 normal, vec3 L, vec3 V, bool shadow)
{
    vec3 albedo = vec3(1.);
   	vec3 diff = max(dot(normal, L) * albedo, 0.) * float(!shadow);

    vec3 refl = normalize(reflect(L, normal));
    float spec = max(dot(refl, -normalize(V)), 0.) * float(!shadow);
    spec = pow(spec, 18.);
    spec = clamp(spec, 0., 1.)*.7;
    float sky = max(0.0, dot(vec3(0.,1.,0.), normal));

    vec3 col = diff * MOON_COLOR;
    col += spec * MOON_COLOR;
    col += sky * vec3(0., .3, 0.5) * .05;

   	return col;
}

// From https://knarkowicz.wordpress.com/2016/01/06/aces-filmic-tone-mapping-curve/
vec3 ACESFilm(vec3 x)
{
    float a = 2.51f;
    float b = 0.03f;
    float c = 2.43f;
    float d = 0.59f;
    float e = 0.14f;
    return clamp((x*(a*x+b))/(x*(c*x+d)+e), 0., 1.);
}

vec4 render( vec2 fragCoord, vec3 iResolution, float iTime, vec4 iMouse, sampler2D gbuffer, sampler2D voronoi, sampler2D particles)
{
    // pixel
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    // fetch precomputed background geometry
    vec4 normalAndZ = texelFetch(gbuffer, ivec2(fragCoord), 0);
    //normalAndZ = vec4(0,0,0,tMax); // disable terrain render
    vec4 state = texelFetch(voronoi, ivec2(0), 0);

    vec3 cameraLookAt, cameraPos, cameraFwd, cameraLeft, cameraUp;
    fxCalcCamera(state, iTime, cameraLookAt, cameraPos, cameraFwd, cameraLeft, cameraUp);

    // camera-to-world and world-to-camera transform
    mat4 c2w = fxCalcCameraMat(iResolution, cameraLeft, cameraUp, cameraFwd, cameraPos);
    mat4 w2c = inverse(c2w);

	vec3 rayDir = fxCalcRay(fragCoord, iResolution, cameraFwd, cameraUp, cameraLeft);
    float zDist = normalAndZ.w;
    vec3 groundPos = cameraPos + rayDir * zDist;

    float shadowHit = 1e6;
    float tMin = .010;
#if SHADOWS
    shadowHit = rayMarching(groundPos + normalAndZ.xyz * 0.03, MOON_DIR, tMin, FAR_CLIP, SHADOW_ITERS);
#endif

    vec4 fragColor;
    fragColor.xyz = lighting(normalAndZ.xyz, MOON_DIR, -cameraFwd, shadowHit < FAR_CLIP); //vec4(groundColor, 0.0) * smoothstep(-50.0, -17.0, -zDist);
    fragColor.a = 1.;

    ivec4 old = fxGetClosestInternal( voronoi, ivec2(fragCoord) );      

    for(int j=0; j<4; j++)
    {
        int particle = old[j];
        if (particle < 0 || particle >= MAX_PARTICLES) continue;
        fxParticle data = fxGetParticleInternal(particles, int(iResolution.x), particle);

        float dim = (1. - smoothstep(.9, 1., data.age)) * smoothstep(0., .1, data.age);
        vec3 oldPos = data.pos;
        vec3 newPos = data.pos + data.vel;
        const vec3 PARTICLE_COLOR = vec3(.7,1,.2);
#if PER_PARTICLE_LIGHTING
        const float GLOW_INTENSITY = 0.03;
        vec3 groundDelta = oldPos - groundPos.xyz;
        float groundDotParticle = dot(groundDelta, normalAndZ.xyz);

        if (groundDotParticle > 0.001)
        {
            float distToGround = length(groundDelta);
            float glow = GLOW_INTENSITY * normalize(groundDotParticle) / (distToGround);
            vec3 glowTemp  = PARTICLE_COLOR * glow * dim;
            fragColor = min(vec4(1), fragColor + vec4(glow * glowTemp, 0.0));
        }
#endif // PER_PARTICLE_LIGHTING

        // convert to camera space
        vec3 oldPosCamera = (w2c * vec4(oldPos,1.0)).xyz;
        oldPosCamera.xy = oldPosCamera.xy / oldPosCamera.z;
        vec3 newPosCamera = (w2c * vec4(newPos,1.0)).xyz;
        newPosCamera.xy = newPosCamera.xy / newPosCamera.z;

        // if in front of clipping plane, not occluded by scene
        if(oldPosCamera.z > 0.01 && newPosCamera.z > 0.01 && zDist > oldPosCamera.z && zDist > newPosCamera.z)
        {
            float dist2 = fxLinePointDist2(oldPosCamera.xy, newPosCamera.xy, p, 1.);
            float dist = sqrt(dist2);

            float PARTICLE_SIZE = 0.02 / oldPosCamera.z;
            float particleTemp = max(0.0, PARTICLE_SIZE*PARTICLE_SIZE - dist2)  / (PARTICLE_SIZE* PARTICLE_SIZE);

            if (dist < PARTICLE_SIZE)
            {
                vec4 pColor = vec4(PARTICLE_COLOR * particleTemp, 1) * dim;
                fragColor = min(vec4(1), fragColor + pColor);
            }
        }
    }

    const float FOG_START = .7;
    if (zDist > FAR_CLIP * FOG_START)
    {
        if (zDist >= FAR_CLIP )
            fragColor.xyz = sky(rayDir, iResolution);
        else
            fragColor.xyz = mix(fragColor.xyz, vec3(0), smoothstep(FAR_CLIP*FOG_START, FAR_CLIP, zDist));
    }
    else if (g_GroundFog)
        fragColor.xyz += GROUND_FOG_COLOR*max(0., .5 - groundPos.y);

    //fragColor.xyz = ACESFilm(sqrt(fragColor.xyz));
    fragColor.w = zDist;
    return fragColor;
}

