// ---------------------------------------------------------------------------------------
// Computes the position and velocity of each particle, one per texture fragment.
// ---------------------------------------------------------------------------------------

const vec3 GRAVITY = vec3(-0.0005,-0.0001,-0.0005);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 iFragCoord = ivec2(fragCoord);
    int index = iFragCoord.x + iFragCoord.y*int(iResolution.x);
    int id = index / NUM_PARTICLE_DATA_TYPES;
    int dataType = index - id * NUM_PARTICLE_DATA_TYPES;
    if(id>=MAX_PARTICLES) return;

    vec4 state = texelFetch(iChannel1, ivec2(0), 0);

    fxParticle p = fxGetParticle(id);

    // Integrate velocities
    vec3 newVel = p.vel + GRAVITY;
    ivec3 newMapPos = ivec3(floor(p.pos + newVel));

    // Limit vertical movement
    if (p.pos.y > 0.4)
    {
        p.pos.y = mix(p.pos.y, 0.4, .6);
    }

    // Detect if we're about to pass through the boundary and bounce off
    float rc = f(p.pos + p.vel, 8);
    if (rc > p.pos.y - .1)// && rc < length(p.vel))
    {
        vec3 g = getNormal(p.pos, 10., 8);
        p.pos.y = mix(p.pos.y, rc + .1, .2);

        newVel.y = abs(newVel.y);
        newVel.xz -= 1.0 * min(0.0, dot(g, newVel)) * g.xz;
    }

    // Ensure constant speed
    float len = length(newVel);
    if (len > 1e-6)
    {
        newVel = PARTICLE_SPEED * normalize(newVel);
    }
    else
    {
        newVel = PARTICLE_SPEED * normalize(vec3(-1, 0, -1));
    }

    p.vel = newVel;

    vec3 newPos = p.pos + newVel;
    float newAge = p.age + min(iTimeDelta, 0.033) / MAX_AGE;

    p.pos = newPos;

    // Reset particles that have gotten too old
    if (iFrame == 0 || newAge > 1.0 || state.w < 0.)
    {
        p.vel = normalize(vec3(-.2, 0, -.2)) * PARTICLE_SPEED;

        vec3 cameraLookAt, cameraPos, cameraFwd, cameraLeft, cameraUp;
        fxCalcCamera(state, iTime, cameraLookAt, cameraPos, cameraFwd, cameraLeft, cameraUp);
        cameraFwd.y = 0.0;

        p.pos.y = 1e6;
        const float XZ_SPREAD = 0.001;
        const float Y_SPREAD = 0.0003;
        const float Y_SPRAY = 0.0;

        for (int i = 0; i < 10; ++i)
        {
            vec4 nse = noise(ivec2(id, id + i)) - .5;
            vec3 pos = cameraPos + nse.xyz * vec3(100, 1, 100);//vec3(0.0, 1.5, 0.0) + p.vel;
            pos.y = 0.;

            if (f(pos, 8) < .1)
            {
                p.pos = pos;
                newAge = nse.w + 0.5;
                break;
            }
        }

    }

    p.age = newAge;

    fragColor = fxSaveParticle(p, dataType);
}

