// ---------------------------------------------------------------------------------------
// Background rendering based on "Glacial Valleys" by jarble
//    https://www.shadertoy.com/view/NscGWl
//
// All of jarble's code is in common, since it's used both here and for the particle
// boundary, and again for the final render if adaptive AA is enabled. This buffer just
// calls raycast to generate a texture full of normals and z distances (the G-buffer).
// ---------------------------------------------------------------------------------------

#define MSAA_N 3 // 2 for 4xMSAA, 3 for 9xMSAA, etc.

vec4 renderGBuffer(vec2 fragCoord, vec3 cameraPos, vec3 cameraFwd, vec3 cameraUp, vec3 cameraLeft, inout float tMin)
{
    vec3 rayDir = fxCalcRay(fragCoord, iResolution, cameraFwd, cameraUp, cameraLeft);

    float t = rayMarching(cameraPos, rayDir, tMin, FAR_CLIP, TERRAIN_ITERS);

    vec3 normal = vec3(0.);

    if (t < FAR_CLIP)
    {
        vec3 p = cameraPos + rayDir * t;
        normal = getNormal(p, t, 12);
    }

    tMin = t; // start next raymarch at the previous t value

    return vec4(normal, t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * .5) / iResolution.y;
    vec4 state = texelFetch(iChannel0, ivec2(0), 0);
    g_GroundFog = state.z == 0.;

    vec3 cameraLookAt, cameraPos, cameraFwd, cameraLeft, cameraUp;
    fxCalcCamera(state, iTime, cameraLookAt, cameraPos, cameraFwd, cameraLeft, cameraUp);

    float tMin = .1;
    float tMax = 0.;

#if MSAA_N > 1
    if (!keyDown(KEY_SHIFT))
    {
        fragColor = vec4(0.);
        for (int x = 0; x < MSAA_N; ++x)
        for (int y = 0; y < MSAA_N; ++y)
        {
            vec2 offset = (vec2(x, y) - .5 * float(MSAA_N - 1)) * 2. / float(MSAA_N);
            vec4 pass = renderGBuffer(fragCoord + offset, cameraPos, cameraFwd, cameraUp, cameraLeft, tMin);

            fragColor += pass;
        }
        fragColor.xyz = normalize(fragColor.xyz);
        fragColor.w = fragColor.w / float(MSAA_N * MSAA_N);
        return;
    }
#endif

    fragColor = renderGBuffer(fragCoord, cameraPos, cameraFwd, cameraUp, cameraLeft, tMin);
}

