void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 state = texelFetch(iChannel1, ivec2(0), 0);
    if (state.w < 0.) { fragColor = vec4(0); return; }
    g_GroundFog = state.z == 0.;

    vec4 new = render(fragCoord, iResolution, iTime, iMouse, iChannel2, iChannel1, iChannel0);

#if TEMPORAL_BLUR
    // Don't apply temporal blur at very high resolutions, assuming frame rate will be crap
    if (iResolution.x * iResolution.y < 1e6 && new.w < FAR_CLIP)
    {
        vec4 old = texelFetch(iChannel3, ivec2(fragCoord), 0);
        float temporalBlur = 1. - .5*smoothstep(3., 8., old.w);
        fragColor = mix(old, new, temporalBlur);
        fragColor.w = new.a;
        return;
    }
#endif

    fragColor = new;
}

