#define S(v,r) smoothstep( r, r+ 3./iResolution.y, v )
float logo(vec2 uv);
const float kRadius = 0.4;
const float kPi = 3.14159;
const float k2Pi = kPi*2.0;
const float kA = kRadius / kPi;

const float rotatespeed = 1.5;

float random (vec2 uv)
{
    return fract(sin(dot(uv.xy, vec2(12.9898,78.233))) * 43758.5453123);
}

vec2 rotate(vec2 uv, float th) {
  return mat2(cos(th), sin(th), -sin(th), cos(th)) * uv;
}

float arclength(float a, float theta) {
	float d = theta * sqrt(abs(1.0 - theta*theta));
	return 0.5 * a * (d*2.0 + log(d));
}

vec3 spiral(vec2 uv)
{
    float ang = atan(uv.y,uv.x);
    float turn = (length(uv)/kRadius - ang/k2Pi);
    ang += ceil(turn)*k2Pi;
	float d = arclength(kA, ang)  - iTime * rotatespeed;
	return vec3(fract(d), fract(turn),floor(d));
}
float dot2( in vec2 v ) { return dot(v,v); }
//https://www.shadertoy.com/view/NslXDM
float sdRoundedCross( in vec2 p, in float h )
{
    float k = 0.5*(h+1.0/h); // k should be const at modeling time
    p = abs(p);
    return ( p.x<1.0 && p.y<p.x*(k-h)+h ) ? 
             k-sqrt(dot2(p-vec2(1,k)))  :
           sqrt(min(dot2(p-vec2(0,h)),
                    dot2(p-vec2(1,0))));
}

//https://iquilezles.org/articles/palettes
vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    vec3 spiral_uv = spiral(uv)-0.5;
    vec2 uv1 = spiral_uv.xy;
    float offset = abs(fract(iTime*0.5)-0.5)*0.2+0.2;
    float startex = smoothstep(0.1,0.05,sdRoundedCross(uv1,1.0)+offset);
    float index = spiral_uv.z;
    vec3 starcolor = (spiral_uv.y+0.3) *
              pal(random(vec2(index)), 
                  vec3(0.9,0.7,0.4),
                  vec3(0.8,0.2,0.9),
                  vec3(1.0,1.0,1.0),
                  vec3(0.3,0.3,0.6) );;
    starcolor = mix(starcolor,vec3(0.2,0.7,1.0),0.5);

    vec3 col = startex*starcolor;
    col +=logo(uv);

    // Output to screen
    fragColor = vec4(col,1.0);
}


float logo(vec2 uv){
float n = 0.;
n += S(.05,abs(length(uv-vec2(-1.23,0))-.12));
n *= 1.-S(-1.25,uv.x);
n += S(.17,abs(uv.y))*S(.05,abs(uv.x+1.29));
n += S(.05,abs(length(uv-vec2(-1.5,0))-.12));
n += S(.05,abs(length(uv-vec2(-0.9,0))-.12));
n += (1.+S(-.6,uv.x)-S(.05,uv.y))*S(.05,abs(length(uv-vec2(-0.6,.03))-.09));
n += (1.-S(-.6,uv.x)+S(-.05,uv.y))*S(.05,abs(length(uv-vec2(-0.6,-.04))-.09));
return n;
}

