vec3 calcNormal( in vec3 pos ){
    vec2 e = vec2(0.0001,0.0);
    return normalize(vec3(map(pos+e.xyy).x-map(pos-e.xyy).x,
                          map(pos+e.yxy).x-map(pos-e.yxy).x,
                          map(pos+e.yyx).x-map(pos-e.yyx).x));

}
vec3 castRay( in vec3 ro, vec3 rd ){
    float m = -1.0;
    float t=0.01;
    float a=0.0;
    for( int i=0; i<100; i++ ){
        vec3 pos = ro + t*rd;
        vec2 h = map(pos);
        m = h.y;
        if(abs(h.x)<0.001)break;
        t += h.x; 
        a++;
        if( t>40.0 ) break;
    }
    if(t>40.0) m=-1.0;
    return vec3( t,m,a );
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;   
    vec2 an = vec2(10.0*iMouse.x/iResolution.x, iMouse.y/iResolution.y) + 2.0;
    vec3 ro = vec3(5.0*sin(an.x),5.0*sin(an.y), 5.0*cos(an.x));
    vec3 ta = vec3(0.0,1.0,0.0);
    vec3 ww = normalize( ta- ro );
    vec3 uu = normalize( cross(ww,vec3(0,1,0)));
    vec3 vv = normalize( cross(uu,ww) );
    vec3 rd = normalize( p.x*uu + p.y*vv + 1.5*ww );
    vec3 col = vec3(0.4,0.75,1.0)-0.7*rd.y;
    col = mix( col, vec3(0.01,0.8,1.0), exp(-10.0*rd.y) );
    vec3 tm = castRay(ro,rd);
    if( tm.y>0.0 ){
        vec3 pos = ro + tm.x*rd;
        vec3 nor = calcNormal(pos);
        vec3 r = reflect(rd, nor);
        vec3 ref = texture( iChannel2, r ).rgb;
        float exponent = 1.0;
        float fresnel = pow(clamp(1. - dot(nor, -rd), 0., 1.), exponent);
        vec3 rimColor = vec3(0.05, 1.5*(pos.y/5.), 0.05);
        vec3 fres = vec3(0.0);
        vec3 mate = vec3(0.18);
        if( tm.y<1.5 ){
            fres = fresnel * rimColor * 1.75;
            float n1 = getNoise(pos*0.01,vec2(0.0),2.0,iTime);
            vec3 yell = vec3(0.1*abs(p.y),0.1*abs(p.y),0.0);
            mate = mix(vec3(0.0,n1,0.0), vec3(1.0,0.4,0.0)*(pos.y+0.2)*0.2, 0.8);
        }else if( tm.y<2.5 ){
            fres = fresnel * rimColor * 1.75;
            mate = vec3(0.2,0.3,0.2);
        }else if( tm.y<3.5 ){
            fres = fresnel * rimColor * 1.75;
            float n1 = getNoise(pos,vec2(0.0),2.0,iTime);
            float n2 = getNoise(pos*.5,vec2(0.0),2.0,iTime)*0.5-0.15;
            pos.y += 0.1*iTime;
            float n3 = getNoise(pos*10.0,vec2(0.0),2.0,iTime)*0.5-0.15;
            ref = texture( iChannel2, r ).rgb+abs(n2);
            mate = mix( ref*vec3(0.2,0.2,0.05),vec3(n2,n1,0.5*(n1+n2)+n3*0.2),0.8);
        }
        vec3  sun_dir = normalize( vec3(0.8,0.4,0.2) );							//Sun Direction
        float sun_dif = clamp( dot(nor,sun_dir),0.0,1.0);						//Sun Difuse
        float sun_sha = step(castRay( pos+nor*0.001,sun_dir ).y,0.0);			//Sun Shadow
        float sky_dif = clamp( dot(0.5+0.5*nor,vec3(0.0,1.0,0.0)),0.0,1.0);	    //Sky Difuse
        float bou_dif = clamp( dot(0.5+0.5*nor,vec3(0.0,-1.0,0.0)),0.0,1.0);	//Bounce Difuse
        col =  mate*vec3(3.0,2.0,2.5)*sun_dif*sun_sha+fres;
        col += mate*vec3(0.5,0.5,3.4)*sky_dif;
        col += mate*vec3(1.0,2.0,0.9)*bou_dif;
        col += vec3(0.01,0.8,1.0)*pow(tm.x*0.01,1.5);
        col *= 0.8;
    }
    col = pow( col, vec3(0.4545) );
    fragColor = vec4(col,1.0);
}

