                +0.25*freqs[2]*freqs[2]
                +0.25*freqs[3]*freqs[3];
    beat = sqrt(beat) * beatScale + beatBias;


/*    float beat2 = 0.3*freqs[3]*freqs[3]
                 +0.4*freqs[4]*freqs[4]
                 +0.3*freqs[5]*freqs[5];
    beat2 = sqrt(beat2) * beatScale + beatBias;*/


    float s = 2.3;


    // animate
	vec2 v1 = vec2(0.0,0.577-0.1)*(s);
	vec2 v2 = vec2(-0.5,-0.289-0.1)*(s);
	vec2 v3 = vec2(0.5,-0.289-0.1)*(s);

    // distance
	float d = sdTriangle( pp, v1, v2, v3 );

    // color
    vec3 col = beat - sign(d)*vec3(0.1,0.4,0.7);

    if (d>0.){
        col += fC.xyz - beat;
    }else{
        col *= 0.8 + 0.2*cos(120.0*d);
        col *= vec3(1.,0.,0.);
    }

    col *= 1.0 - exp(-2.0*abs(d));
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.02,abs(d)) );

	fragColor = vec4(col,1.0);
}

