	float prev = 0.;
	float tw = 0.;
	for (int i = 0; i < 18; ++i) {
		float mag = dot(p, p);
		p = abs(p) / mag + vec3(-.5, -.4, -1.5);
		float w = exp(-float(i) / 7.);
		accum += w * exp(-strength * pow(abs(mag - prev), 2.2));
		tw += w;
		prev = mag;
	}
	return max(0., 5. * accum / tw - .7);
}

vec3 nrand3( vec2 co )
{
	vec3 a = fract( cos( co.x*8.3e-3 + co.y )*vec3(1.3e5, 4.7e5, 2.9e5) );
	vec3 b = fract( sin( co.x*0.3e-3 + co.y )*vec3(8.1e5, 1.0e5, 0.1e5) );
	vec3 c = mix(a, b, 0.5);
	return c;
}

// signed distance to a 2D triangle
float sdTriangle( in vec2 p, in vec2 p0, in vec2 p1, in vec2 p2 )
{
	vec2 e0 = p1 - p0;
	vec2 e1 = p2 - p1;
	vec2 e2 = p0 - p2;

	vec2 v0 = p - p0;
	vec2 v1 = p - p1;
	vec2 v2 = p - p2;

	vec2 pq0 = v0 - e0*clamp( dot(v0,e0)/dot(e0,e0), 0.0, 1.0 );
	vec2 pq1 = v1 - e1*clamp( dot(v1,e1)/dot(e1,e1), 0.0, 1.0 );
	vec2 pq2 = v2 - e2*clamp( dot(v2,e2)/dot(e2,e2), 0.0, 1.0 );

    float s = e0.x*e2.y - e0.y*e2.x;
    vec2 d = min( min( vec2( dot( pq0, pq0 ), s*(v0.x*e0.y-v0.y*e0.x) ),
                       vec2( dot( pq1, pq1 ), s*(v1.x*e1.y-v1.y*e1.x) )),
                       vec2( dot( pq2, pq2 ), s*(v2.x*e2.y-v2.y*e2.x) ));

	return -sqrt(d.x)*sign(d.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 pp = (2.0*fragCoord.xy-iResolution.xy)/iResolution.y;
	pp *= 1.5;

//////////////////////
    vec2 uv = 2. * fragCoord.xy / iResolution.xy - 1.;
	vec2 uvs = uv * iResolution.xy / max(iResolution.x, iResolution.y);
	vec3 p = vec3(uvs / 4., 0) + vec3(1., -1.3, 0.);
	p += .2 * vec3(sin(iTime / 16.), sin(iTime / 12.),  sin(iTime / 128.));

	float freqs[6];
	//Sound
	freqs[0] = texture( iChannel0, vec2( 0.01, 0.25 ) ).x;
	freqs[1] = texture( iChannel0, vec2( 0.03, 0.25 ) ).x;
	freqs[2] = texture( iChannel0, vec2( 0.05,  0.25 ) ).x;
	freqs[3] = texture( iChannel0, vec2( 0.07, 0.25 ) ).x;
	freqs[4] = texture( iChannel0, vec2( 0.15, 0.25 ) ).x;
	freqs[5] = texture( iChannel0, vec2( 0.30, 0.25 ) ).x;

	float t = field(p,freqs[4]);
	float v = (1. - exp((abs(uv.x) - 1.) * 6.)) * (1. - exp((abs(uv.y) - 1.) * 6.));

    //Second Layer
	vec3 p2 = vec3(uvs / (4.+sin(iTime*0.11)*0.2+0.2+sin(iTime*0.15)*0.3+0.4), 1.5) + vec3(2., -1.3, -1.);
	p2 += 0.25 * vec3(sin(iTime / 16.), sin(iTime / 12.),  sin(iTime / 128.));
	float t2 = field2(p2,freqs[5]);
	vec4 c2 = mix(.4, 1., v) * vec4(1.3 * t2 * t2 * t2 ,1.8  * t2 * t2 , t2* freqs[0], t2);

    	//Let's add some stars
	//Thanks to http://glsl.heroku.com/e#6904.0
	vec2 seed = p.xy * 2.0;	
	seed = floor(seed * iResolution.x);
	vec3 rnd = nrand3( seed );
	vec4 starcolor = vec4(pow(rnd.y,40.0));

	//Second Layer
	vec2 seed2 = p2.xy * 2.0;
	seed2 = floor(seed2 * iResolution.x);
	vec3 rnd2 = nrand3( seed2 );
	starcolor += vec4(pow(rnd2.y,40.0));


    vec4 fC = fieldBias+fieldScale*(mix(freqs[5]-.3, 1., v) * vec4(1.5*freqs[4] * t * t* t , 1.2*freqs[3] * t * t, freqs[5]*t, 1.0)+c2)+starScale*starcolor;


//////////////////

    float beat = 0.25*freqs[0]*freqs[0]
                +0.25*freqs[1]*freqs[1]
                +0.25*freqs[2]*freqs[2]
                +0.25*freqs[3]*freqs[3];
    beat = sqrt(beat) * beatScale + beatBias;


/*    float beat2 = 0.3*freqs[3]*freqs[3]
                 +0.4*freqs[4]*freqs[4]
                 +0.3*freqs[5]*freqs[5];
    beat2 = sqrt(beat2) * beatScale + beatBias;*/


    float s = 2.3;


    // animate
	vec2 v1 = vec2(0.0,0.577-0.1)*(s);
	vec2 v2 = vec2(-0.5,-0.289-0.1)*(s);
	vec2 v3 = vec2(0.5,-0.289-0.1)*(s);

    // distance
	float d = sdTriangle( pp, v1, v2, v3 );

    // color
    vec3 col = beat - sign(d)*vec3(0.1,0.4,0.7);

    if (d>0.){
        col += fC.xyz - beat;
    }

    col *= 1.0 - exp(-2.0*abs(d));
	col *= 0.8 + 0.2*cos(120.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.02,abs(d)) );

