// CC0: Impossible refractions
//  Tinkering with an old shader + new distance field
//  Looked interesting enough to share.

#define TIME            iTime
#define RESOLUTION      iResolution

#define PI              3.141592654
#define PI_2            (0.5*PI)
#define TAU             (2.0*PI)

#define TOLERANCE       0.0001
#define MAX_RAY_LENGTH  12.0
#define MAX_RAY_MARCHES 60
#define NORM_OFF        0.001
#define MAX_BOUNCES     5
#define ROT(a)          mat2(cos(a), sin(a), -sin(a), cos(a))

mat3 g_rot  = mat3(1.0); 
vec3 g_mat  = vec3(0.0);
vec3 g_beer = vec3(0.0);

// License: WTFPL, author: sam hocevar, found: https://stackoverflow.com/a/17897228/418488
const vec4 hsv2rgb_K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
vec3 hsv2rgb(vec3 c) {
  vec3 p = abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www);
  return c.z * mix(hsv2rgb_K.xxx, clamp(p - hsv2rgb_K.xxx, 0.0, 1.0), c.y);
}
// License: WTFPL, author: sam hocevar, found: https://stackoverflow.com/a/17897228/418488
//  Macro version of above to enable compile-time constants
#define HSV2RGB(c)  (c.z * mix(hsv2rgb_K.xxx, clamp(abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www) - hsv2rgb_K.xxx, 0.0, 1.0), c.y))

const vec3 skyCol     = HSV2RGB(vec3(0.6, 0.86, 1.0));
const vec3 lightPos   = vec3(0.0, 10.0, 0.0);

const float initt       = 0.1; 

// License: Unknown, author: nmz (twitter: @stormoid), found: https://www.shadertoy.com/view/NdfyRM
vec3 sRGB(vec3 t) {
  return mix(1.055*pow(t, vec3(1./2.4)) - 0.055, 12.92*t, step(t, vec3(0.0031308)));
}

// License: Unknown, author: Matt Taylor (https://github.com/64), found: https://64.github.io/tonemapping/
vec3 aces_approx(vec3 v) {
  v = max(v, 0.0);
  v *= 0.6f;
  float a = 2.51f;
  float b = 0.03f;
  float c = 2.43f;
  float d = 0.59f;
  float e = 0.14f;
  return clamp((v*(a*v+b))/(v*(c*v+d)+e), 0.0f, 1.0f);
}

// License: MIT, author: Pascal Gilcher, found: https://www.shadertoy.com/view/flSXRV
float atan_approx(float y, float x) {
  float cosatan2 = x / (abs(x) + abs(y));
  float t = PI_2 - cosatan2 * PI_2;
  return y < 0.0 ? -t : t;
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float torus(vec3 p, vec2 t) {
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float hex(vec2 p, float r) {
  const vec3 k = vec3(-0.866025404,0.5,0.577350269);
  p = abs(p);
  p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;
  p -= vec2(clamp(p.x, -k.z*r, k.z*r), r);
  return length(p)*sign(p.y);
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/articles/distfunctions/
float hexTorus(vec3 p, vec3 d) {
  vec2 q = vec2(length(p.xz) - d.x, p.y);
  float a = atan_approx(p.x, p.z);
  mat2 r = ROT(1.0*a);
  return hex(r*q, d.y)-d.z;
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float box(vec2 p, vec2 b) {
  vec2 d = abs(p)-b;
  return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/www/articles/intersectors/intersectors.htm
float rayPlane(vec3 ro, vec3 rd, vec4 p) {
  return -(dot(ro,p.xyz)+p.w)/dot(rd,p.xyz);
}

mat3 rot_z(float a) {
  float c = cos(a);
  float s = sin(a);
  return mat3(
      c,s,0
    ,-s,c,0
    , 0,0,1
    );
}

mat3 rot_y(float a) {
  float c = cos(a);
  float s = sin(a);
  return mat3(
      c,0,s
    , 0,1,0
    ,-s,0,c
    );
}

mat3 rot_x(float a) {
  float c = cos(a);
  float s = sin(a);
  return mat3(
      1, 0,0
    , 0, c,s
    , 0,-s,c
    );
}

vec3 skyColor(vec3 ro, vec3 rd) {
  vec3 col = clamp(vec3(0.0025/abs(rd.y))*skyCol, 0.0, 1.0);

  float tp0  = rayPlane(ro, rd, vec4(vec3(0.0, 1.0, 0.0), 4.0));
  float tp1  = rayPlane(ro, rd, vec4(vec3(0.0, -1.0, 0.0), 6.0));
  float tp = tp1;
  tp = max(tp0,tp1);


  if (tp1 > 0.0) {
    vec3 pos  = ro + tp1*rd;
    vec2 pp = pos.xz;
    float db = box(pp, vec2(6.0, 9.0))-1.0;

    col += vec3(4.0)*skyCol*rd.y*rd.y*smoothstep(0.25, 0.0, db);
    col += vec3(0.8)*skyCol*exp(-0.5*max(db, 0.0));
  }

  if (tp0 > 0.0) {
    vec3 pos  = ro + tp0*rd;
    vec2 pp = pos.xz;
    float ds = length(pp) - 0.5;

    col += vec3(0.25)*skyCol*exp(-.5*max(ds, 0.0));
  }

  return clamp(col, 0.0, 10.0);
}

float df10(vec3 p) {
  vec3 mat = vec3(0.9, 0.85, 1.5);
  const vec3 gcol = -4.0*(HSV2RGB(vec3(0.05, 0.925, 1.0)));
  vec3 beer = gcol;

  float d0 = hexTorus(p, vec3(2.0, 0.65, 0.025));
  float d1 = torus(p, vec2(2.0, 0.25));
  float d = d0;

  d = max(d, -(d1- 0.05));
  if (d1 < d) {
    const vec3 gcol = -10.*(HSV2RGB(vec3(0.55, 0.5, 1.0)));
    beer = gcol;
    d = d1;
  }

  g_mat = mat;
  g_beer = beer;

  return d;
}


float df(vec3 p) {
  p *= g_rot;
  p = p.xzy;
  return df10(p);
}

vec3 normal(vec3 pos) {
  vec2  eps = vec2(NORM_OFF,0.0);
  vec3 nor;
  nor.x = df(pos+eps.xyy) - df(pos-eps.xyy);
  nor.y = df(pos+eps.yxy) - df(pos-eps.yxy);
  nor.z = df(pos+eps.yyx) - df(pos-eps.yyx);
  return normalize(nor);
}

float rayMarch(vec3 ro, vec3 rd, float dfactor, out int ii) {
  float t = 0.0;
  float tol = dfactor*TOLERANCE;
  ii = MAX_RAY_MARCHES;
  for (int i = 0; i < MAX_RAY_MARCHES; ++i) {
    if (t > MAX_RAY_LENGTH) {
      t = MAX_RAY_LENGTH;    
      break;
    }
    float d = dfactor*df(ro + rd*t);
    if (d < TOLERANCE) {
      ii = i;
      break;
    }
    t += d;
  }
  return t;
}

vec3 render(vec3 ro, vec3 rd) {
  vec3 agg = vec3(0.0, 0.0, 0.0);
  vec3 ragg = vec3(1.0);

  bool isInside = df(ro) < 0.0;

  for (int bounce = 0; bounce < MAX_BOUNCES; ++bounce) {
    float dfactor = isInside ? -1.0 : 1.0;
    float mragg = min(min(ragg.x, ragg.y), ragg.z);
    if (mragg < 0.025) break;
    int iter;
    float st = rayMarch(ro, rd, dfactor, iter);
    const float mrm = 1.0/float(MAX_RAY_MARCHES);
    float ii = float(iter)*mrm;
    vec3 mat = g_mat;
    if (st >= MAX_RAY_LENGTH) {
      agg += ragg*skyColor(ro, rd);
      break; 
    }

    vec3 sp = ro+rd*st;

    vec3 sn = dfactor*normal(sp);
    float fre = 1.0+dot(rd, sn);
//    fre = clamp(abs(fre), 0.0, 1.0);
    fre *= fre;
    fre = mix(0.1, 1.0, fre);

    vec3 ld     = normalize(lightPos - sp);

    float dif   = max(dot(ld, sn), 0.0); 
    vec3 ref    = reflect(rd, sn);
    float re    = mat.z;
    float ire   = 1.0/re;
    vec3 refr   = refract(rd, sn, !isInside ? re : ire);
    vec3 rsky   = skyColor(sp, ref);
    const vec3 dcol = HSV2RGB(vec3(0.6, 0.85, 1.0));
    vec3 col = vec3(0.0);    
    col += dcol*dif*dif*(1.0-mat.x);
    float edge = smoothstep(1.0, 0.9, fre);
    col += rsky*mat.y*fre*vec3(1.0)*edge;
    if (isInside) {
      ragg *= exp(-st*g_beer);
    }
    agg += ragg*col;

    if (refr == vec3(0.0)) {
      rd = ref;
    } else {
      ragg *= mat.x;
      isInside = !isInside;
      rd = refr;
    }

    // TODO: if beer is active should also computer it based on initt    
    ro = sp+initt*rd;
  }

  return agg;
}

vec3 effect(vec2 p) {
  float a = 0.05*TIME;
  g_rot = rot_x(2.0*a)*rot_y(3.0*a)*rot_z(5.0*a);
  vec3 ro = vec3(0.0, 2.0, 5.0);
  const vec3 la = vec3(0.0, 0.0, 0.0);
  const vec3 up = vec3(0.0, 1.0, 0.0);

  vec3 ww = normalize(la - ro);
  vec3 uu = normalize(cross(up, ww ));
  vec3 vv = normalize(cross(ww,uu));
  const float fov = tan(TAU/6.);
  vec3 rd = normalize(-p.x*uu + p.y*vv + fov*ww);

  vec3 col = render(ro, rd);

  return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1. + 2. * q;
  p.x *= RESOLUTION.x/RESOLUTION.y;
  vec3 col = vec3(0.0);
  col = effect(p);
  col = aces_approx(col); 
  col = sRGB(col);
  fragColor = vec4(col, 1.0);
}

#define DISTANCE_THRESHOLD 0.01
#define GLOW_RADIUS 0.05

// Inigo Quilez segment sdf
// https://iquilezles.org/articles/distfunctions2d/
float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;

    // Center bar coordinates
    float x = 0.35*cos(iTime);
    float y = 0.35*sin(iTime);

    float x2 = (0.35*cos(iTime + radians(180.0)));
    float y2 = (0.35*sin(iTime + radians(180.0)));

    float dist = sdSegment(uv, vec2(x,y), vec2(x2,y2));

    vec3 col = vec3(0.0,0.0,0.0);

    if (dist < DISTANCE_THRESHOLD) {
        // Inside center bar
        col = vec3(0.0,1.0,0.0);
    } else if (dist < GLOW_RADIUS) {
        // Outside bar, but within glow radius
        float glow = GLOW_RADIUS * abs(sin(iTime));
        float distUV = dist * (1.0/glow);
        float g = smoothstep(1.0,0.0,distUV);
        col = vec3(0.0,g,0.0);
    } else {
        // Outside glow radius
        float ringFrequency = 10.0;
        float ringDecay = 30.0;
        float g = step(0.8, fract(dist * ringFrequency)) / floor(dist * ringDecay);
        col = vec3(0.0,g,0.0);
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

// Simple busy HUD - https://youtu.be/qzMAGAn3QE8

#define PI 3.141519
#define TAU 6.283185

mat2 rot(float a)
{
    float c = cos(a);
    float s = sin(a);
    return mat2(c, -s, s, c);
}

// dashed circle - https://www.shadertoy.com/view/7tyGWw
float sdDashedCircle(in vec2 p, float at, float inner,float outer,float nseg,float rat) 
{
    float le = length(p);
    float hw = (outer-inner)*0.5;
    float ce = le - (outer-hw);
    ce = abs(ce)-hw;
    //float at = atan(p.x,p.y);
    float gr = fract((at/TAU)*nseg); 
    gr = abs((gr-0.5)*2.0);
    gr = gr-rat;
    float an = gr * (PI/nseg);
    mat2 rm = rot(an);
    float c = rm[0][0];//cos(an);
    float ep = clamp(c*le,inner,outer);
    vec2  ne = p*rm;
    ne *= ep/le;  
    float ed = length(p-ne);
    return (an>0.0) ? ed : (ce>0.0) ? ce : max(-ed,ce);
}

vec4 GetTimer(in vec2 p,float fill)
{
    vec3 col1 = vec3(0.95,0.75,0.25);    // orange
    vec3 col2 = vec3(0.2,0.2,0.2);    // grey    
    float totalsegs = 60.0;
    float extra = 40.0;
    float thresh = totalsegs+(extra*0.5);
    fill *= (totalsegs+1.0+extra);

    float at = atan(p.x,p.y);
    float count = floor(at/(TAU/totalsegs)) + totalsegs*0.5;

    float fade = clamp((fill-count)*0.1,0.0,1.0);
    float fade2 = 1.0;
    if (fill>thresh)
        fade *= 1.0-clamp((fill-thresh)*0.05,0.0,1.0);

    // render circle
    float rad = 0.8;
    float height = mix(0.02,0.04,fade);    
    float inner = rad-height;
    float outer = rad+height;
    float rt = 0.65;//float rt = mix(0.25,0.6,fade);
    float d =  sdDashedCircle(p,at,inner,outer,totalsegs,rt);
    float aa = 1.5*rt*fwidth(at); //2./iResolution.y; // 0.005
    float cc = smoothstep(aa,0.0,d);
    return vec4(mix(col2*cc,col1*cc,fade),cc);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    float t = iTime;
    float fill = fract(t*0.4);
    fragColor = GetTimer(-p,fill);
}

const float PI = 3.14159265359f;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = (fragCoord.xy / iResolution.xy);
