float getFreq(float x) {
	return texture(iChannel0, vec2(floor(x * rn + 1.) / rn, 0)).x;
}


float getFreq_smooth(float x) {
	float index = floor(x * rn) / rn;
    float next = floor(x * rn + 1.0) / rn;
	return mix(getFreq(index), getFreq(next), smoothstep(0.0, 1.0, fract(x * rn)));
}

float getFreq_blend(float x) {
    return mix(getFreq(x), getFreq_smooth(x), 0.5);
}

#define pi 3.14159
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec2 ouv = uv;

    float freq = getFreq_smooth(abs(uv.x));
	vec3 col = vec3(freq);

    float v = getFreq_smooth(0.);
    uv.x = abs(uv.x);
    uv.y += 0.15;
    vec2 o = vec2(0.05 + 0.2 * v * v, 0.25);
    float d = length(uv - o);
    float t = -pi/4. + v * pi/2. + pi / 8. * cos(2. * pi * v);
    float d2 = arc(-uv + vec2(0,0.2*v), 
                   vec2(cos(t), sin(t)),
                   0.25* v * v, 0.);

    float thk = 0.01; //0.005 + 0.01 * v;
    float k = 1. / iResolution.y;
    float s = smoothstep(-k, k, abs(-d + 0.1 * v * v) - thk);
    float s2 = smoothstep(-k ,k, abs(-d2 + 0.1 * pow(v,2.5)) - thk);
    float d3 = length(uv - o);

    uv.y -= 0.15;
    float d4 = length(uv);
    float s3 = smoothstep(-k, k, -d3 + 0.05 * v * v);
    float s4 = smoothstep(-k, k, abs(-d4 + 0.2 + 0.2 * v) - thk);

    col = vec3((s * s2-s3) * s4);
    float r2 = .2 + .2 * v;
    vec3 col2 = col * (1.-pow(1.-v,2.)) * 
               (step(d4,r2) + exp(-(20. + 20. * (1.-v)) * abs(d4 - thk - r2)));
    float tnh = tanh(0.25 * max(0., iTime - 4.));
    //if (iTime > 7. * pi) 
    //    tnh = pow(0.5 - 0.5 * thc(5., 1. * iTime), 40.);
    // Uncomment this if you want to lose your eyesight
    // (STROBE WARNING)
    //tnh *= 0.5 + 0.5 * thc(4., 30. * iTime + 0. * pi * v);

    col = mix(col, col2, tnh);
    //col = mix(col, 0.5 + vec3(ouv.x, ouv.y, 0.), 0.1);
    col = mix(col, col * vec3(1,v,1.-v) * (0.85 + uv.y), tnh);
    fragColor = vec4(col, 1.0);
}

