vec2 getDist(vec3 p) { //     
	vec3 sphere = vec3(0.,0.,0.);
    vec3 n = normalforlit(p);

    vec2 st = p.xy;

    st.y+= iTime*0.1;

     // 
    float mashtab = 15.;
    st *= mashtab;

    //  
    vec2 i_st = floor(st);
    vec2 f_st = fract(st);

    float m_dist = 100.0;
    for (int y= -1; y <= 1; y++) {
        for (int x= -1; x <= 1; x++) {
            //  
            vec2 neighbor = vec2(float(x),float(y));
            vec2 point = random2(i_st + neighbor);
            point = 0.5+0.5*sin(iTime + 6.2831*point);
            vec2 diff = neighbor + point - f_st; //  
            float dist = length(diff) + 1.*sin(point.x); //   
            m_dist = min(m_dist, dist); //   
        }
    }

    //float line = 1./(iResolution.y/mashtab);
    //vec3 col = vec3(m_dist);
    //fragColor = vec4(col,1.0);


	float sphereDist = dist(p, sphere) - m_dist*0.1 - 1.3 ;
    float fraktal_noise = dist(p, sphere)  - fbm(vec2(n.x*4.+iTime*0.7, n.y*4.))*0.7 - 1. ; 

    float d = smin(sphereDist, fraktal_noise, 0.1);
    //  + fbm(vec2(n.x*5.+iTime, n.y*5.))*0.5
    //  
    float t = 0.;
    if (sphereDist>fraktal_noise) t = 1.;

	//float d = sphereDist;
	return vec2(d, t);

}


vec3 normal(vec3 p) {
	float d = getDist(p).x;
	float p1 = getDist(sub(p, EX)).x;
	float p2 = getDist(sub(p, EY)).x;
	float p3 = getDist(sub(p, EZ)).x;
	vec3 tri = vec3(p1, p2, p3);
	vec3 n = sub(vec3(d,d,d), tri);
	return normalize(n);
}





float light(vec3 p) {
	//vec3 lightPos = vec3(-10., 4., 0.);
    vec3 lightPos = vec3(sin(iTime)*3., 1., cos(iTime)*3.);
	vec3 lightDir = normalize(sub(lightPos, p));
	vec3 n = normalforlit(p);
	return dot(n, lightDir) * 0.5  + 0.5;
}

vec3 rayMarching(vec3 ro, vec3 rd) {
	vec3 p = ro;
	for (int i = -0; i < 500; i++){
		float d = getDist(p).x;
        float t = getDist(p).y;
		if (d > MAX_DIST) {break;}
		p = sum(p, mul(rd, d));
		if (d < 0.0001){
			//let dif = i / ITERATION
			//dif = dif+dif
            float l = light(p);
            float n = 2.;
			return vec3(
            pow(l*t,5.),
            pow(l,n),
            pow(l*(1.-t),n)

            );
		} 
	}
	return vec3(0.,0.,0.);
}

