        d = min(d,d2);
        grd = prevGrd;        

        float d3 = B(grd-vec2(0.,0.24),vec2(0.46,0.22));

        float dir = (n>=0.75)?-1.:1.;
        grd.x += dir*iTime*0.2;
        d2 = blocks(grd-vec2(0.,0.24));
        d2 = max(d3,d2);
        d2 = min(d2,abs(d3)-0.01);
        d = min(d,d2);
    } else if(n>=0.8 && n<0.9){
        // left
        grd-=vec2(-0.24,0.24);
        grd*=scale;
        d = drawFont(grd,char);
        grd = prevGrd;        

        grd -= vec2(-0.24,-0.24);
        grd*=scale;
        d2 = drawFont(grd,(char+1>=35)?10:char+1);
        d = min(d,d2);
        grd = prevGrd;     


        float d3 = B(grd-vec2(0.24,0.0),vec2(0.22,0.46));

        grd-=vec2(0.24,0.0);
        d2 = dSlopeLines(grd);
        d2 = max(d3,d2);
        d2 = min(d2,abs(d3)-0.01);
        d = min(d,d2);
    } else if(n>=0.9 && n<1.){
        // right
        grd-=vec2(0.24,0.24);
        grd*=scale;
        d = drawFont(grd,char);
        grd = prevGrd;            

        grd -= vec2(0.24,-0.24);
