#define OCTAVES 8

mat2 rotate2D(float r) {
    return mat2(cos(r), sin(r), -sin(r), cos(r));
}

float fbm(in vec2 uv,int octaves)
{
    //this function generates the terrain height
    float value = 0.,
    value1=value,
    amplitude = 2.;
    uv /= 32./4.;
    vec2 t1 = vec2(0.);
    mat2 r = rotate2D(12.);
    vec2 uv1 = uv;
    for (int i = 0; i < octaves; i++)
    {
        //t1 *= rotate2D((t1.x+t1.y));
        t1 =
            //(triwave(uv-triwave(uv1*r/2.15))-t1.yx)
            triwave(uv)
            //(triwave(uv1-triwave(uv*r))-t1.yx)
            //(triwave(uv/2.15-triwave(uv1*r))-t1.yx)
        ;
        value1=sqrt(value1*value1+value*value+.01);
        value = abs(abs(t1.x-t1.y) * amplitude-value);
        amplitude /= 2.15;
        uv1 = uv;
        uv = (uv.yx*2.15 + t1)*r;
    }

    return value1;
}

float fbm(in vec2 uv){
    return fbm(uv,OCTAVES);
}

