//#define THRESHOLD_SCALE_BIAS
//#define THRESHOLD_CUT
#define THRESHOLD_SMOOTHSTEP

#if defined (THRESHOLD_SCALE_BIAS)
vec2 threshold(in float threshold, in float colour)
{
    float upper = (colour - threshold) * (1.0 / (1.0 - threshold));
    //float lower = -min(0.0, (colour - threshold)) * (1.0 / threshold);
    float lower = colour - upper;
    return vec2(clamp(upper, 0.0, 1.0), lower);
}
#elif defined(THRESHOLD_CUT)
vec2 threshold(in float threshold, in float colour)
{
    float upper = (colour < threshold)?(0.0):(colour);
    float lower = (colour < threshold)?(colour):(0.0);
    return vec2(upper, lower );
}
#elif defined (THRESHOLD_SMOOTHSTEP)
vec2 threshold(in float threshold, in float colour)
{
    float upper = smoothstep(0.0, 1.0 - threshold, colour - threshold);
    float lower = colour - upper;
    return vec2(upper, lower);
}
#endif

vec3 apply_filter_result(vec3 rgb, float filter_result)
{
    if (mod(iTime, 10.0) < 5.0)
    {
        return rgb * (1.0 - filter_result);
    }
    else
    {
        return vec3(1.0 - filter_result);
    }
}

/*
// Cycle between full screen application of the effect and a set of
// animating bands showing different aspects of the filter and the
// effect of applying the threshold
*/
bool showFullScreenEffect()
{
    //return true;
    return mod(iTime, 20.0) > 10.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 coord = ivec2(fragCoord);
    vec4 filter_result = texelFetch(iChannel0, coord, 0).rgba;

    vec3 colour = filter_result.rgb;
    float scharr = filter_result.a;

    /*
    // Threshold test to remove some of the finer edges.
    // Cycle it over time to show the effect of variation
    */
    float thres_cycle = 0.5 + 0.3 * sin(iTime * 2.0);
    vec2 thres = threshold(thres_cycle, scharr);

    if (showFullScreenEffect())
    {
        thres = threshold(0.6, scharr);
        colour *= (1.0 - thres.r);
    }
    else
    {
        // Just some maths to draw the bars of different outputs
        float res_l1 = iResolution.x + iResolution.y;
        float wipe  = (res_l1 * 0.3) * cos(iTime * 0.5);
        float bar_s = (res_l1 * 0.10);
        float bar_m = (res_l1 * 0.50);
        float bar_e = (res_l1 * 0.90);

        float fragCoord_l1 = fragCoord.x + fragCoord.y;

        if (fragCoord_l1 < bar_s + wipe)
        {
            // Original Image
            colour = filter_result.rgb;
        }
        else if (fragCoord_l1 < bar_s + wipe + 4.0)
        {
            colour *= 0.5;
        }
        else if (fragCoord_l1 < bar_m + wipe)
        {
            // Edges that passed the threshold test
            colour = apply_filter_result(colour, thres.r);       
        }
        else if (fragCoord_l1 < bar_m + wipe + 4.0)
        {
            colour *= 0.5;
        }
        else if (fragCoord_l1 < bar_e + wipe)
        {
            // Original Scharr Result
            colour = apply_filter_result(colour, scharr);
        }
        else if (fragCoord_l1 < bar_e + wipe + 4.0)
        {
            colour *= 0.5;
        }
        else
        {
            // The edges below the threshold we're excluding
            colour = apply_filter_result(colour, scharr - thres.r);
        }
    }

    fragColor.rgb = colour;
    //fragColor.rgb = filter_result.aaa;
}

