    float aspect_screen = iResolution.x / iResolution.y;
    float aspect_image = iChannelResolution[CHANNEL_IDX].x / iChannelResolution[CHANNEL_IDX].y;

    if (aspect_image > aspect_screen)
    {
        uv.x *= aspect_screen / aspect_image;
    }
    else
