/*
// Can do this better, but this works for now while I work out how much of a
// blur I want prior to the Scharr filter being applied. This is helpful on
// noisier input.
*/
float WideSeparableL(ivec2 fragCoord, ivec2 delta, const float taps[TAPS/2+1])
{
	float ret;
	ivec2 coord = fragCoord - (TAPS/2) * delta;

	ret = taps[0] * luminance(texelFetch(iChannel0, coord, 0).rgb);
	coord += delta;
	ret += taps[1] * luminance(texelFetch(iChannel0, coord, 0).rgb);
	coord += delta;
#if (TAPS >= 5)
	ret += taps[2] * luminance(texelFetch(iChannel0, coord, 0).rgb);
	coord += delta;
#if (TAPS >= 7)
	ret += taps[3] * luminance(texelFetch(iChannel0, coord, 0).rgb);
	coord += delta;
#if (TAPS >= 9)
	ret += taps[4] * luminance(texelFetch(iChannel0, coord, 0).rgb);
	coord += delta;
#if (TAPS >= 11)
	ret += taps[5] * luminance(texelFetch(iChannel0, coord, 0).rgb);
	coord += delta;
#if (TAPS >= 13)
	ret += taps[6] * luminance(texelFetch(iChannel0, coord, 0).rgb);
	coord += delta;
#if (TAPS >= 15)
	ret += taps[7] * luminance(texelFetch(iChannel0, coord, 0).rgb);
	coord += delta;
	ret += taps[6] * luminance(texelFetch(iChannel0, coord, 0).rgb);
	coord += delta;
#endif
	ret += taps[5] * luminance(texelFetch(iChannel0, coord, 0).rgb);
	coord += delta;
#endif
	ret += taps[4] * luminance(texelFetch(iChannel0, coord, 0).rgb);
	coord += delta;
#endif
	ret += taps[3] * luminance(texelFetch(iChannel0, coord, 0).rgb);
	coord += delta;
#endif
	ret += taps[2] * luminance(texelFetch(iChannel0, coord, 0).rgb);
	coord += delta;
#endif
	ret += taps[1] * luminance(texelFetch(iChannel0, coord, 0).rgb);
	coord += delta;
#endif
	ret += taps[0] * luminance(texelFetch(iChannel0, coord, 0).rgb);

	return ret;
}


/*
// Chooses a set of gaussian filter taps based upon defines and passes
// them to the separable filter function above
*/
float gaussian_filter_3x3(in ivec2 fragCoord)
{
#if defined (SEPARABLE_15)
	const float taps[8] = float[](
		// Sig = 3.0
		//0.009033,	0.018476,	0.033851,	0.055555,	0.08167,	0.107545,	0.126854,	0.134032
        // Sig = 1.4
		0.000002,	0.000041,	0.000611,	0.005556,	0.030863,	0.104916,	0.218504,	0.279015
	);
#elif defined (SEPARABLE_9)
	const float taps[5] = float[5](
		// Sigma = 3
		//0.063327, 0.093095, 0.122589, 0.144599, 0.152781
		// Sigma = 2
		0.028532, 0.067234, 0.12400, 0.179044, 0.20236
		// Sigma = 1.4
		//0.005563, 0.030904, 0.105053, 0.21879, 0.27938
		// Sigma = 1 -- Nice round shape, but almost too much falloff, makes the 
		// outer taps of too low a value - especially in multi pass filtering
		//0.000229, 0.005977, 0.060598, 0.241732, 0.382928
	);
#elif defined (SEPARABLE_5)
    const float taps[3] = float[3](
        // Sigma = 3
        //0.1784, 0.210431, 0.222338
        // Sigma = 2
        0.153388, 0.221461, 0.250301
        // Sigma = 1.4
        //0.113318, 0.236003, 0.30136
        // Sigma = 1
        //0.06136, 0.24477, 0.38774
        // NOP
        //0.0, 0.0, 1.0
    );
#else /*defined (SEPARABLE_3)*/
    const float taps[2] = float[2](
        // Sigma = 3
        //0.327162, 0.345675
        // Sigma = 2
        0.319466, 0.361069
        // Sigma = 1.4
        //0.305163, 0.389673
        // Sigma = 1
        //0.27901, 0.44198
        // NOP
        //0.0, 1.0
    );
#endif

    float x = WideSeparableL(fragCoord, ivec2(1,0), taps);
    float y = WideSeparableL(fragCoord, ivec2(0,1), taps);

    return 0.5 * (x + y);
